style.css:
@-moz-document domain("bancodeseries.com.br") {
@namespace url(http://www.w3.org/1999/xhtml);
body,.well,.accordion-body,.accordion-heading{
    background: #17151c;
  color: whitesmoke !important;
  }
  
  .navbar-inner{
    background-image: none !important;
    background-color:  black !important;
  }

 #grmft-ads{
	 display: none;
 }

.navbar-inverse {
    background-color: #1e1c25;
}

.breadcrumb{
  background-color: #1e1c25;
}

a, .text-info{
  color: #723f8c;
}

.label-primary{
  background-color: #723f8c;
}

.label-primary[href]:hover, .label-primary[href]:focus{
  background-color: #723f8c;
}

.modal-content{
  background-color: #1e1c25;
}

.btn-primary{
    background-color: #723f8c;
  border:none;
}

.panel-transparent{
      background: #1e1c25;
  border: none;
}

.scrollex>li>a{
        background: #723f8c;
  color: whitesmoke !important;
}

.img-responsive, .thumbnail>img, .thumbnail a>img, .carousel-inner>.item>img, .carousel-inner>.item>a>img{
  border-radius: 10px;
}

.thumbnail{
  background: none;
  border: none;
}

.btn-primary:hover{
      background-color: #1e1c25;
}

.table>thead>tr>td.info, .table>tbody>tr>td.info, .table>tfoot>tr>td.info, .table>thead>tr>th.info, .table>tbody>tr>th.info, .table>tfoot>tr>th.info, .table>thead>tr.info>td, .table>tbody>tr.info>td, .table>tfoot>tr.info>td, .table>thead>tr.info>th, .table>tbody>tr.info>th, .table>tfoot>tr.info>th{
       background-color: #1e1c25;
}

.panel-groups{
   	background: #17151c;
  	border: none;
}

.panel-footer{
     	background: #17151c;
  	border: none;
}

.panel-default>.panel-heading{
  background: none;
}

.panel-group .panel{
  background-color: #17151c;
  border-color: #723f8c;
}

.navbar-inverse .navbar-nav>li>a:hover, .navbar-inverse .navbar-nav>li>a:focus, .navbar-inverse .btn-link:hover, .navbar-inverse .btn-link:focus{
  color: #723f8c;
}

.adsbygoogle, .adsbygoogle-noablate, #cto_banner_content{
  display:none!important;
}
}
