style.css:
::selection {
    background: hsl(210deg 0% 50% / 20%);
    color: inherit;
}

::-webkit-scrollbar-thumb {
    background-color:  hsl(210deg 0% 50% / 60%);
    border-radius: 1000px;
}


::-webkit-scrollbar-thumb:hover {
    background-color:  hsl(210deg 0% 50% / 80%);
    border-radius: 10px;
}

::-webkit-scrollbar-thumb:active {
    background-color:  hsl(210deg 0% 50% / 100%);
}

::-webkit-scrollbar{
  background:inherit;
    width: 10px;
}
