style.css:
/* 字体样式，字体阴影 */
*:not([class*="icon"]):not(i) {
  font-family: "金山云技术体" !important;
  font-weight: 300; /* 设置字体细腻程度 */
  transition: color 0.3s ease-in-out; /* 添加过渡动画 */
}

body {
  font-size: 16px;
  line-height: 1.6;
  color: #333;
  background-color: #f5f5f5;
  letter-spacing: 0.5px;
}

p {
  margin: 1em 0;
  text-align: left;
  color: #3c3c3c; /* 调整为更易于长时间阅读的颜色 */
}

/* 设置背景色为更易于长时间阅读的颜色 */
body {
  background-color: #fafafa;
}

/* 添加暗色模式 */
@media (prefers-color-scheme: dark) {
  body {
    color: #fff;
    background-color: #333;
  }

  p {
    color: #bbb;
  }
}

/* 调整响应式设计 */
@media screen and (max-width: 768px) {
  body {
    font-size: 14px;
  }
}

@media screen and (min-width: 769px) and (max-width: 1024px) {
  body {
    font-size: 15px;
  }
}

@media screen and (min-width: 1025px) {
  body {
    font-size: 16px;
  }
}
