style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("forum.ea.com") {
/* remove background pic */
.main { background-image:none !important }

/* remove unneeded elements */
td.mainHeader, #footer, #gus-header, td.row1[height="28"] span.gensmall, td.row2[height="28"] span.gensmall, hr { display:none !important }

/* make things wide */
#headerTable, div.main, #contentTable, #forumContentTable { width: 100% !important }
 
/* make things more readable */
span.postbody, span.genmed, td.postinfo, span.topictitle, a.forumlink, span.forumlink { font-size: 0.9em !important; font-family: Arial !important }
span.postdetails { font-size: 0.8em !important }
a:link, a:visited { text-decoration: none !important }
}
