style.css:
@-moz-document domain('qidian.com') {
/* 全黑背景*/
.wrap, 
.top-nav,
.top-nav > .box-center,
.name,
.footer, .footer .box-center,
#numero5 > li,
.lbf-button,
a.act,
.filter-search-wrap,
.shelf-search,
.lbf-combobox-panel .lbf-combobox-item,
.top-nav .sign-in i.black,
.focus-wrap .notice-wrap, 
.focus-wrap .week-rec-wrap,
.channel-focus,
.channel-header .search-wrap .search-input,
.lbf-autocomplete-suggestions,
.lbf-autocomplete-group strong,
.book-info h1,
.book-detail-mid,
body
{
  background-color: black !important;
  color: #cccccc !important;
}
/* 深黑背景*/
.search-box,
cite,
#notice,
.edit-rec-wrap .edit-rec-list li,
.hover-box,
.hot-work-slide,
#numero4 .left-info,
.update-wrap .god-wrap .god-list, 
.update-wrap .update-rec-list,
.god-list ,
.new-rec-wrap .left-info,
.slider-wrap,
.sub-type-info,
.ui-tab-contents,
.ui-tab-tabs,
.ui-tab-tabs .checked, .ui-tab-tabs .checked a,
.shelf-content,
.shelf-table,
.readerClockWrapper,
.readerClockWrapper .header h2,
.sideBar-group li:hover .sideBar-list-a,
.page-bottom-fixed .bototm-qrcode,
.page-bottom-fixed .page-bottom-item,
.intro-honor-label, .ticket-reward,
.book-catalog,
.honor-detail .title h2,
.intro-detail .title h2,
.book-weekly-hot-rec,
.right-section-con,
.home-msg-wrapper
{
  background-color: #181818 !important;
  color: #cccccc !important;
}
.ui-table tr:hover,
.noReadStatus
{
  background-color: #282828 !important;
  color: #d8d8d8 !important;
}
/* 浅黑背景 */
.book-shelf,
.shelf-foot.fixed,
.scroll,
.book-catalog .catalog-header,
.honor-works, .other-works, .rec-works,
.left-nav .act,
.blue-btn-detail:hover,
.ui-select-button {
  background-color: #282828 !important;
  color: #d8d8d8 !important;
}
.page-bottom-fixed .icon-code,
.work-number .icon-right {
  filter: invert(0.5);
}
.ui-table
{
  color: #a0a0a0 !important;  
}
.ui-table tbody td{
  border-bottom: 1px solid #626262;
}
.classify-list,
.focus-wrap .notice-wrap .notice,
.logo-wrap .book-shelf a{
  border: 1px solid #666;
}
/* 深灰背景*/
.book-catalog .volume-name,
.book-catalog .volume-header,
.left-nav .act:hover,
#ticket-Tab .mon-dynamic, 
#ticket-Tab .rec-dynamic, 
#ticket-Tab .reward-dynamic,
.red-vote-btn, 
.all-label .red  {
  background-color: #444 !important;
  color: #ccc !important;  
}
/* 灰色文字 */
.crumbs-nav-new .bread_crumbs_book_name,
.crumbs-nav-new a,
.all-works-title,
.book-info .author-name .writer,
.book-weekly-hot-rec .section-title,
.honor-detail p, .intro-detail p,
.honor-works .work-title h3, 
.honor-news strong,
.other-works .work-title h3, 
.rec-works .work-title h3,
.right-section-con .right-section-title,
.book em, 
.days em,
.write em,
.count em,
.month-ticket .num,
.rec-ticket .num, 
.reward-text .num
{
  color: #888 !important;  
}
.crumbs-nav-new a{
  opacity: .7;
}
/* 红色文字 */
.book-catalog .catalog-newest-con .link
{
  color: #b33 !important;  
}
.book-catalog .catalog-newest-con .link:hover
{
  color: #e00 !important;  
}
/* 高亮 */
.sideBar-group li.active{
  background-color: #202020 !important;
  color: #dddddd !important;  
}
.sideBar-operate {
  background: repeating-linear-gradient(to right,transparent,#404040 10px,#606060);
}
/* 分割线 */
 .rank-list .book-list li .name-box,
.book-list-wrap .book-list li,
.score-list li {
    border-top: 1px solid #787878;
}
table {
  border-color: #303030 !important;
}
a:link,
a:link h2,
h3.wrap-title {
  color: #bbb !important;  
}
a:visited {
  color: #777 !important;
}
a.channel:visited,
.classify-list dl dd A:visited {
  color: #ddd !important;
}
a:visited:after {
  color: red !important;
  content: '.' !important;
}

/* 书籍简介*/
.edit-rec-wrap .edit-rec-list li p,
.book-info p,
.edit-rec-wrap .focus-today .description p,
p.intro,
.info-text .intro{
  color: #aaa !important;
  font-family:system-ui;
}

.filter-search-wrap .box-center {
  background-color: #222222 !important;
  color: #cccccc !important;
}

.lbf-slides > ul {
  filter: brightness(0.75);
}
.focus-img,
.banner-item,
.safety-img {
  filter: brightness(0.5);
}
.icon-add,
.icon-edit,
.icon-disband {
  filter: brightness(2);
}
.home-msg {
  border-left: 1px solid #303030;
}
.ui-tab-contents {
  border: 1px solid #606060;
}
.ui-tab-tabs,
.ui-tab-tabs .checked, .ui-tab-tabs .checked a{
  border: 1px solid #606060;
  border-bottom: 0;
}
.qdp-badge-a, .qdp-border, .qdp-hovertips, .qdp-list {
  border: 1px solid #202020;
}
}
