style.css:
/* Apply dark mode to the entire website */
body {
  background-color: #121212 !important;
  color: #ffffff !important;
}

/* Change button colors to match the dark theme */
button {
  background-color: #4e8cff !important;
  color: #ffffff !important;
}

/* Apply dark mode to forms */
input[type="text"],
input[type="password"],
textarea {
  background-color: #282828 !important;
  color: #ffffff !important;
  border: 1px solid #444444 !important;
}

/* Style form buttons */
input[type="submit"],
button[type="submit"] {
  background-color: #4e8cff !important;
  color: #ffffff !important;
}

/* Style table headers */
th {
  background-color: #333333 !important;
  color: #ffffff !important;
}

/* Style table rows */
tr:nth-child(odd) {
  background-color: #1e1e1e !important;
}

/* Style table cells */
td {
  border: 1px solid #444444 !important;
}
