style.css:
@-moz-document domain("andro-news.com") {
 
/* Відступ від шапки */
 .header {
     padding-bottom: 20px;
}
/* Лист новин */
 .news__item:hover img.news__img {
     transition-delay: 0.5s !important;
     transition: 2s;
     transform: scale(1.05);
     filter: saturate(1.3);
}
 .news__item img.news__img {
     transition-delay: 0.0s !important;
     transition: 2s;
     transform: scale(1);
}
 .news__pic {
     overflow: hidden;
  }
.page-category a.news__item>div {
    width: 310px !important;
    padding-right: 0px;
    margin-right: 10px;
}



/* Віджети */
 .rightbar {
     margin-top: 0px;
}
 .widget__title{
     margin-bottom: 5px;
     margin-top: 0px;
     font-family: Bebas Neue Pro !important;
     font-size: 22px !important;
}
 .comments-last__item {
     font-family: bebas neue pro;
     font-size: 18px;
     padding-bottom: 10px;
     margin-bottom: 15px !important;
}
 .comments-last__info {
     margin-bottom: 20px;
}
 .comments-last__post {
     font-family: bebas neue pro;
     font-size: 17px;
     margin-bottom: 25px;
     color: #666;
}
 .comments-last__date {
     font-size: 14px;
}
 .comments-last__user {
     color: #666;
     background: 0 3px/13px no-repeat;
}
 a.comments-last__news {
     font-weight: 600;
     color: #777;
}
 .comments-last__date {
     background: 0 2px/12px no-repeat;
     padding-left: 20px;
}
 .news__title {
     font-family: bebas neue pro;
     font-size: 16px !important;
     color: #666;
}
/* Тайтли новин */
 h1.content__title.page-news__title {
     font-family: bebas neue pro;
     font-size: 38px;
     margin: 15px 0 20px 0;
}
 .breadcrumbs.content__breads {
     display: none;
}
 .page-category .news .news__title {
     font-family: bebas neue pro;
     font-size: 21px !important;
     color: #666;
}
 .wysiwyg h2 * {
     font-family: Bebas Neue Pro !important;
     font-size: 24px !important;
}
 h2 {
     font-family: Bebas Neue Pro !important;
     font-size: 24px !important;
}
/* Слайдер */
 .slick-arrow {
     transition: 0.5s;
}
 .slick-arrow:hover {
     opacity: 1;
     transition: 0.5s;
     filter: brightness(1.2);
}
 .slick-prev:hover:before, .slick-next:hover:before {
     opacity: 1;
}
 .slick-dots li button:hover {
     background: #69cff8;
     filter: brightness(1.2);
     transition: 0.5s;
}
 .slick-dots li button {
     transition: 0.5s;
}
 .slick-slide a img:hover {
     transform: scale(1.1);
     filter: saturate(1.5) !important;
     transition: 5s;
     transition-delay: 0.5s;
}
 .slick-slide a img {
     transition: 3s;
     transition-delay: 1.5s;
}
 
  



}
