style.css:
{"result":"@-moz-document domain('app.slack.com') {
  .p-ia4_client.p-ia4_client--with-search-in-top-nav .p-view_contents--primary, .p-ia4_client.p-ia4_client--with-search-in-top-nav .p-view_contents--secondary, .p-ia4_client.p-ia4_client--with-search-in-top-nav .p-view_contents--sidebar {
    max-height: calc(100vh - 50px);
    top: 50px;
  }

  .p-ia4_client .p-client_workspace__layout {
    background: var(--dt_color-base-pry);
    position: fixed;
    height: 100%;
    left: 0;
    top: 0;
    width: 100%;
    z-index: 2000;
  }

  .p-ia4_top_nav {
    z-index: 2001;
  }

  
  .p-tab_rail {
    background: var(--dt_color-base-pry) !important;
    position: fixed;
    height: 50px;
    top: 0;
    z-index: 2003;
  }

  .p-tab_rail:hover {
    height: 100vh;
  }

  .p-peek_trigger__overlay {
    left: 10px;
  }

  .p-team_switcher_popover__container {
    position: relative;
    left: 70px;
  }

  .p-control_strip {
    height: 70px;
    overflow: hidden;
    z-index: 2003;
    zoom: 0.7;
  }

  .p-control_strip:hover {
    background: var(--dt_color-base-pry);
    border-top-right-radius: 8px;
    height: auto;
    padding-top: 10px;
    zoom: 1;
  }

  .ReactModal__Overlay {
    z-index: 2003;
  }


  .ReactModal__Content {
    z-index: 2004 !important;
  }
}"}
