style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 

domain("4chan.org")
{

/*[[junk]]*/


/*[[lgdisplay]]*/ .rules{
display:none !important;
}


html,body {
background: url(http://i.imgur.com/9TtDC.jpg) bottom right no-repeat fixed #fff !important;
color:#FFF !important;
font-size:14px !important;
font-family:"Segoe UI", sans-serif !important;
}

span[class="quote"]{
    color:#170 !important;
}

.postarea{
display:none !important;
opacity:0.6 !important;
position:fixed !important;
top:30% !important;
right:-460px !important;
background:url(http://i.imgur.com/zG94y.png) repeat-y !important;
padding:5px 5px 0 5px!important;
-moz-border-radius-topleft:10px !important;
-moz-border-radius-bottomleft:10px !important;
border-top-left-radius:10px !important;
border-bottom-left-radius:10px !important;
-moz-transition: right 0.5s, opacity 0.5s;
}

[class="post reply"]{
background:url(http://i.imgur.com/zG94y.png) repeat-y !important;
}

.postarea label{
position:relative !important;
top:-23px !important;
left:-105px !important;
}

.postarea:hover{
right:-133px !important;
height:auto !important;
opacity: 1 !important;
}

.deletebuttons{
background:url(http://i.imgur.com/JmZhR.png)!important;
position:fixed !important;
right:-320px !important;
bottom:10% !important;
padding:0 5px !important;
opacity: 0.6 !important;
-moz-border-radius-topleft:10px !important;
-moz-border-radius-bottomleft:10px !important;
border-top-left-radius:10px !important;
border-bottom-left-radius:10px !important;
-moz-transition: right 0.5s, opacity 0.5s;
}

.deletebuttons:hover{
right:0px !important;
opacity: 1 !important;
}

#imgControls{
background:url(http://i.imgur.com/JmZhR.png), #fef0d2 !important;
position:fixed !important;
right:-130px !important;
top:50px !important;
color:#8D8375 !important;
padding:1px 5px !important;
width:205px !important;
-moz-border-radius-topleft:10px !important;
-moz-border-radius-bottomleft:10px !important;
border-top-left-radius:10px !important;
border-bottom-left-radius:10px !important;
opacity: 0.6 !important;
-moz-transition: right 0.5s, opacity 0.5s;
}

.reply{
-moz-border-radius: 10px !important;
border-radius:10px !important;
padding:2px !important;
}

#imgControls:hover{
right:0px !important;
opacity: 1 !important;
}

_blank {
font-size:0%
}

body {
margin:0 !important;
padding:0 !important;
width:60% !important;
}

.adminbar {
float:none !important;
text-align:center !important;
font-size:0;
}

table[border="0"] tbody tr td {
border:none !important
}

a,input[type="submit"],input[type="button"],input[type="reset"] {
cursor:pointer !important
}

a{
background-color:transparent !important;
}

a,a img,span.plus {
color:#2b7274!important
}

#header {
display:none
}

[class*="postername"] {
color:#2b7274 !important
}

[class*="postertrip"] {
color:#2b7274 !important
}

.omittedposts,.abbr,.thumbnailmsg {
color:#CCC !important
}

.unkfunc{
color:#060 !important;
font-style: italic !important;
}

a:hover {
text-decoration:underline !important
}

#footer{
position:fixed !important;
bottom:-30px !important;
background:#fff !important;
width:100% !important;
padding:0 !important;
}


h1,h2,h3,a[title*="Permanent link to "]:hover,.permalink,.highlight,.replyhl,#watchedthreadsdraghandle,.replymode,.postblock,input:not([type="submit"]):not([type="button"]):not([type="reset"]),select,textarea,th[bgcolor="#286f73"],a img.thumb,a img[src*="/thumb/"],.tn_reply,.tn_thread {
-moz-border-radius: 6px !important;
border-radius:6px !important;
border-width: 1px !important;
border-style: solid !important;
border-color: #777777 !important;
background-color:#e4f6fa !important;
}

.replymode,.postblock,input:not([type="submit"]):not([type="reset"]),select,textarea,th[bgcolor="#fff"]{
background: #fff !important;
opacity: 1
}

.content,.reply,#watchedthreads,.nigratrain {
background-color:#e4f6fa !important;
background: url(http://i.imgur.com/JmZhR.png) repeat-y !important;
opacity: 1
}

/*a img.thumb,a img[src*="/thumb/"],.tn_reply,.tn_thread,.boxcontent img,[id^="4chan_ext"][class="reply"] {
border:1px dashed #e4f6fa !important
}*/

/*a:hover,a img:hover,.tn_reply:hover,.tn_thread:hover,.boxcontent img:hover,#header {
border-color:#FFF !important
}*/


.postername,.commentpostername,[class="4chan_qr_link"] {
font-size:100% !important;
text-decoration:none !important

}

.filesize,.filesize *{
font-size:0% !important
}

.reflink *:hover,.quotejs:hover {
font-weight:400 !important
}

.spoiler,.spoiler:before {
background-color:#b3e4de !important;
color:#b3e4de !important
}

font[color="#fff"] b,.spoiler:before,.mod,.oldpost,.admin,[class*="postername"] [style*="color:"] {
color:#fff !important;
font-weight:700 !important
}

#twi_table ,#twi_table_div > .postblock,.reply,.replyhl{
border:0 !important
}

[id^="4chan_ext"][class="reply"],.reflinkpreview {
background-color:#e4f6fa !important
}

.highlight {
border-color:#fff !important
}

a:hover,a img:hover,span.plus:hover,ul li a:focus,.replymode,.reply,h1,h2,h3,h4,h5,h6,.spoiler:hover,.filetitle,.filesize,.replytitle,.logo,#watchedthreadsdraghandle,.postblock,input[type="text"],input[name="postpassword"],input[name="meiru"],input[name="kotehan"],.filetitle,.filesize,.replytitle,.logo,#watchedthreadsdraghandle,.postblock,input[type="text"],input[name="postpassword"],input[name="meiru"],input[name="kotehan"],input[type="password"],input[name="name"],input[name="subject"],input[name="em"],select[name="formatting"],textarea,th[bgcolor="#fff"],.replyhl,.highlight,.spoiler:hover,span.spoiler:hover,.board .hborderform,form,form *:not(a):not(font):not(b){
color:#2b7274 !important
}

img.thumb,img[src*="/thumb/"] {
background:transparent !important
}

.content{
padding:5px !important;
font-size:0% !important;
}

.content>h2{
margin:-5px -5px 0 -5px !important;
padding:0 3px !important;
line-height:1.4em !important;
font-size:125% !important;
}

.pages{
border:solid 0px #e4f6fa !important;
background:transparent !important;
}


#qp{
background:#b3e4de !important;
padding:2px !important;
}

/********** BUTTONS **********/

[type="submit"],[type="button"],[type="reset"]{
vertical-align:top !important;
height:1.5333em !important;
display:inline !important;
padding:0px 2px!important;
border: 1px #888 !important;
color:#286f73 !important;
background:#e4f6fa !important;
margin:0 1px !important;
}

[type="submit"]:hover,[type="button"]:hover,[type="reset"]:hover{
border-color:#FFF !important;
color:#FFF !important;
background:#286f73 !important
}
[type="submit"]:active,[type="button"]:active,[type="reset"]:active{
position: relative;
top: 1px;
left: 1px; 
}
[type="checkbox"]{
margin:3px !important;
}
}
