@-moz-document regexp("https://(greasy|sleazy)fork.org/([^/]+/)?scripts/\\d+.*?/feedback.*") {
  /* SCRIPT FEEDBACK *******************************************************************/
  #script-links {
    z-index: 99;
    position: relative;
  }
  #script-content {
    background: linear-gradient(white 11ex, /*[[bgcolor]]*/
    11ex);
    position: relative
  }
  #script-content > h3:first-child {
    display: none
  }
  #script-content > p {
    padding: 0 2ex 0 calc(96px + 3ex)
  }
  #script-content > p#support-url {
    padding-top: 1rem;
    padding-bottom: 1rem;
  }
  #script-content > h3 {
    padding: 1ex 2ex 0 calc(96px + 2.5ex)
  }
  #contribution,
  #support-url {
    background-color: white;
    margin-bottom: -1em !important;
    padding: 2ex 2ex 0 2rem !important;
  }
  #support-url + .post-discussion > h3 + :not(p) {
    margin-top: 2em !important;
  }
  .post-discussion h3 {
    display: none
  }
  .post-discussion h3 + p {
    margin-top: 0;
    margin-bottom: 0;
    padding: 2ex 0 2ex 2rem !important;
    font-weight: bold
  }
  .post-discussion a[href="#post-discussion"] {
    margin-left: 2rem;
  }
  #start-discussion-top {
    font-weight: bold
  }
  #discussions {
    display: block;
    margin-left: -0.5ex;
    margin-top: 3ex;
    background-color: /*[[bgcolor]]*/;
    padding-bottom: 0;
  }
  h3 + p + #discussions {
    margin-top: 1ex
  }
  h3:first-child + #discussions {
    margin-top: 6ex;
    padding-top: 2ex
  }
  h3:first-child + #discussions + p {
    position: absolute;
    top: -1em;
  }
  #no-discussions + h3#feedback-favoriters {
    background-color: /*[[bgcolor]]*/;
  }
  #discussions li {
    display: table-row;
    color: transparent;
    font-size: 0;
    padding: 4px 0 0;
    position: relative;
  }
  #discussions li > *:first-child::before {
    width: 16px;
    height: 16px;
    display: block;
    content: " ";
    position: absolute;
    margin-left: -30px;
  }
  .discussion-question > *:first-child::before {
    background-image: url(https://greasyfork.org/images/circle-blue.png);
    opacity: 0.1
  }
  .discussion-ok > *:first-child::before {
    background-image: url(https://greasyfork.org/images/circle-yellow.png)
  }
  .discussion-good > *:first-child::before {
    background-image: url(https://greasyfork.org/images/circle-green.png)
  }
  .discussion-bad > *:first-child::before {
    background-image: url(https://greasyfork.org/images/circle-red.png)
  }
  .discussion-report > *:first-child::before {
    background-image: url(https://greasyfork.org/images/report.png)
  }
  #discussions li:nth-child(even) {
    background: rgba(0, 20, 40, 0.05)
  }
  #discussions li > * {
    display: table-cell;
    padding: 0.7ex 2ex 0.5ex 1ex;
    vertical-align: middle;
    line-height: 1.1;
    font-size: 11pt;
  }
  #discussions li a {
    text-decoration: none
  }
  #discussions li time {
    color: initial;
    white-space: nowrap
  }
  #discussions li a:first-child {
    font-size: 12pt
  }
  #discussions + p {
    font-weight: bold;
    padding-top: 2ex;
    padding-bottom: 2ex;
    margin-bottom: 0
  }
  #feedback-favoriters + div {
    padding-left: calc(96px + 3.5ex);
    padding-right: 2ex;
    font-size: 10pt;
    line-height: 1.2;
  }
  h3#feedback-favoriters {
    padding-bottom: 1ex;
    margin-top: 0;
    background-color: /*[[bgcolor]]*/;
  }
  #script-content > form {
    padding-left: calc(96px + 3ex);
  }
  .post-discussion form {
    padding: 1rem 1rem 1rem 2rem;
  }
}
