style.css:
@-moz-document domain("solutiocloud.us") {
/***********Custom Scrollbar****************/
/* Hide the default scrollbar */
::-webkit-scrollbar {
    width: .75rem;
}

::-webkit-scrollbar-track {
    background-color: #f5f5f5;
}

::-webkit-scrollbar-thumb {
    background-color: #ccc;
    border-radius: 5px;
}

/* Style the scrollbar on hover */
::-webkit-scrollbar-thumb:hover {
    background-color: #bbbbbb;
}

/* Style the scrollbar when it's being dragged */
::-webkit-scrollbar-thumb:active {
    background-color: #8c8c8c;
}
}

@-moz-document url("https://solutiocloud.us/scrum-worksheet") {
.container {
    padding: 0;
}

.bigcontainer:nth-child(2) {
    padding: 0;
}

#tag_box_al, #tag_box_jackie {
    display: none;
}
}

@-moz-document url("https://solutiocloud.us/landing") {
#tag_box_al, #tag_box_jackie {
        display: none;
    }
    
    .pagetitle {
      color: transparent;
      display: none;
    }
    
    .pageheader > .container::after {
      content: 'Super Cool Casa';
      top: 50%;
      left: 50%;
      width: 100%;
      transform: translate(-50%, 0%);
      position: relative;
      display: block;
      color: #f1f1f1;
      text-align: center;
      padding: 2rem;
      font-size: 4vw;
      text-shadow: 1px 1px 2px rgba(0,0,0,0.5);
    }
    
    body > div.bigcontainer > div.pageheader.gearbackground > div {
      max-width: 100%;
    }
    
    body > div.bigcontainer > div.bigcontainer > div:nth-child(4) > div:nth-child(4),
    body > div.bigcontainer > div.bigcontainer > div:nth-child(4) > div:nth-child(5),
    body > div.bigcontainer > div.bigcontainer > div:nth-child(4) > div:nth-child(3) {
      display: none;
    }
}
