style.css:
@-moz-document url(https://bsu.instructure.com/) {
body{
  background-color: #fffbfd !important;
}
a {
    color: #f1636f;
    text-decoration: none;
}
.ic-app-header__logomark-container {
    width: 100%;
    background-color: #fffbfd;
    box-sizing: border-box;
}
.ic-app-header {
    background-color: #fb6d79;
}
.ic-app-header__logomark {
    display: block;
    overflow: hidden;
    background-image: url(https://pbs.twimg.com/media/EBjI_2yX4AEtJH5.jpg);
    background-position: center;
    background-repeat: no-repeat;
    background-size: 100%;
    transition: transform 0.3s;
    background-color: rgb(251 109 121);
}
.icon-hamburger {
    color: #fb6d79;
}
.item-group-condensed .ig-header {
    background-color: rgb(251 109 121);
    border: rgb(251 109 121);
}
.ig-header .name {
    color: white;
    text-shadow: 0px 0px;
    font-weight: normal;
}
span {
    color: #ff0016;
}
.ig-list .ig-row:hover {
    background-color: #fb6d7912;
}
#right-side .events_list li.event i.icon-check, #right-side .events_list li.todo i.icon-check, #right-side .to-do-list li.event i.icon-check, #right-side .to-do-list li.todo i.icon-check {
    color: #ff0016;
}
.accessible-toggler {
    outline-width: 0.125rem;
    outline-color: transparent;
    outline-style: solid;
    color: #fff;
}
.item-group-condensed .ig-header {
    color: #ffffff;
}
.fQfxa_dqAF.fQfxa_eCSh {
    background: #ff9ca4;
    border-color: var(--fQfxa-primaryBorderColor);
    color: var(--fQfxa-primaryColor);
}
.btn.active, .btn.Button--active, .Button.active, .active.ui-button, .Button.Button--active, .Button--active.ui-button, .ui-button.ui-state-active:hover, .ui-button.ui-state-active, .ui-progressbar .ui-button.ui-widget-header {
    background: #ff6a76;
    border-color: #ff0d0d;
    color: #fff;
}
.group_user_348734, .group_user_348734:hover, .group_user_348734:focus {
    color: #ff0016;
    border-color: #ff0016;
    background-color: #fb6d79;
}
#calendar-app .fc-month-view .fc-today, #calendar-drag-and-drop-container .fc-month-view .fc-today {
    background: #fff1f1;
}
.btn-primary, .Button--primary {
    background: #ff9da5;
    color: var(--ic-brand-button--primary-text);
    border: 1px solid;
    border-color: var(--ic-brand-button--primary-bgd-darkened-15);
}
.btn-link, .Button--link {
    border-color: transparent;
    cursor: pointer;
    color: #ff0016;
}
.message-list .message-count {
    font-size: 13px;
    font-size: 0.8125rem;
    background-color: #fb6d79;
    padding: 4px 7px;
    border-radius: 4px;
    text-shadow: none;
}
.yyQPt_cjfS .yyQPt_cSXm {
    background: #fb6d79;
    border-color: var(--yyQPt-checkedBorderColor);
}
a.fbyHH_bGBk, button.fbyHH_bGBk {
    color: #bd7878;
    cursor: pointer;
}
#footer .footer-logo {
    background-color: transparent;
    background-image: url(https://pa1.narvii.com/7048/3a7bafeffe981a65f893c23a652eeceba711a680r1-320-181_hq.gif);
    background-repeat: repeat;
    background-position: center;
    display: inline-block;
    height: 30px;
    margin: 0 20px 0 0;
    width: 1000px;
}
.ig-list .ig-row.ig-published:not(.student-view):before {
    content: "";
    box-sizing: border-box;
    position: absolute;
    top: -1px;
    left: -1px;
    width: 0.25rem;
    height: calc(100% + 2px);
    background-color: #fb6d7975;
    border-top: 1px solid #fb6d79;
    border-bottom: 1px solid #fb6d79;
}
  }
