style.css:
/* Retrowave Background CSS */
body {
  background-color: #000; /* Set background color to black */
  background-image: linear-gradient(90deg, #8f00ff, #ff008e); /* Gradient background */
  background-size: 400% 400%; /* Increase the gradient size for a wave effect */
  animation: gradientAnimation 10s linear infinite; /* Animation for the gradient */
  background-blend-mode: overlay; /* Blend mode for the gradient */
  color: #fff; /* Text color */
  font-family: Arial, sans-serif; /* Choose a suitable font */
}

@keyframes gradientAnimation {
  0% {
    background-position: 0% 50%;
  }
  100% {
    background-position: 100% 50%;
  }
}
