style.css:
{"result":"@-moz-document domain('zhuanlan.zhihu.com'), domain('www.zhihu.com'){
/*------------对于所有卡片的处理------------*/
.Card {
    border-radius: 0em;	/*卡片去圆角*/
  	overflow: hidden;
    transition-duration: 0.4s;
}
/*------------所有用户头像的处理------------*/
.AppHeader-profileAvatar{	/*右上角自己的头像：圆角、放大、点击动效*/
  border-radius: 50%;
  transform: scale(1.15);
  transition: .15s ease-out;
}
.AppHeader-profileAvatar:hover{
  transform: scale(1.2);
}
.AppHeader-profileAvatar:active{
  transform: scale(1.1);
  transition: .05s ease-out;
}
.Avatar.AuthorInfo-avatar{	/*首页条目、回答、文章左上方的作者头像：圆角、放大*/
  border-radius: 50%;
  transform: scale(1.1);
}
.AuthorInfo-avatar.css-1u1mpgw{	/*首页“推荐关注“：排除头像放大””*/
  border-radius: 50%;
  transform: none;
}
.Avatar.css-1mw6vzc{	/*评论区打字框左侧自己头像*/
  border-radius: 50%;
}
.Avatar.css-bxt5wn{	/*评论区头像：圆角、悬停动画*/
  border-radius: 50%;
  transform: scale(1.1);
  transition: .3s ease-out;
}
.Avatar.css-bxt5wn:hover{
  transform: scale(1.1) rotate(360deg);
}
/*------------所有按钮的处理------------*/
.Button{	/*所有按钮*/
  border-radius: 1000px;
  transition: .2s ease-out;
}
/*蓝色按钮*/
.Button--primary.Button--blue{
  border: none !important;
  background-color: #3c7dff !important;
  box-shadow: 0px 3px 5px #3c7dff40;
  transition: .08s;
}
.Button--primary.Button--blue:hover{
  background-color: #3c7dff !important;
  box-shadow: 0px 6px 13px #3c7dff80;
  transition: .2s ease-out;
}
.Button--primary.Button--blue:active{
  box-shadow: none;
  background-color: #0461cf !important;
  transition: .1s ease-out;
}
/*赞同和踩按钮的圆角和动画效果*/
.VoteButton:active{	/*被鼠标按下的赞同或踩按钮*/
  background-color: rgba(5,109,232,.30) !important;
}
.VoteButton--up{	/*赞和踩按钮：平滑过渡、去除夹在中间的圆角*/
  border-top-right-radius: 0px;
  border-bottom-right-radius: 0px;
  border-top-left-radius: 1000px;
  border-bottom-left-radius: 1000px;
   transition: .2s ease-out !important;
}
.VoteButton--down{
  border-top-left-radius: 0px;
  border-bottom-left-radius: 0px;
  border-top-right-radius: 1000px;
  border-bottom-right-radius: 1000px;
   transition: .2s !important;
}
.VoteButton--up.is-active{	/*“已赞同”按钮：左右都有圆角*/
  border-radius: 1000px;
  padding:  0 16px !important;
}
.VoteButton--up.is-active > span > svg{	/*“已赞同”按钮的小三角*/
  transition: .2s ease-out;
  transform: scale(1.2)  translate(-1px, .3px);
}
.VoteButton--up.is-active + button{	/*"已赞同"按钮右边的踩按钮：隐藏*/
  transform: scale(0);
  padding: 0 0 !important;
  border: 0 !important;
  margin: 0 !important;
  width: 0 !important;
}
/*------------用户主页和知乎首页顶端Tabs悬停效果------------*/
.Tabs-link:after{
  transition: .3s ease;
	sbackground: transparent;
	bottom: -1px;	/*错位修正*/
	content: "";
	height: 3px;	/*高度*/
	left: 0; /*删去则消失*/
	position: absolute;
	right: 0;
}
.Tabs-link:hover:after{
	background: #558eff80;	/*悬停则半透明显示*/
}
.Tabs-link.is-active:after{
	background: #558eff !important;	/*已选中的项不受影响*/
	width: auto !important;
}
/*------------超链接修改------------*/
/*不可见部分显示和隐藏省略号*/
.invisible{
  display: inline !important;
  font-size: 15px !important;
  color: #ffffff !important;
}
.ellipsis{
  display: none;
}
/*回答内容链接*/
html[data-theme=dark] .ztext a.external:hover, html[data-theme=dark] .ztext a.internal:hover, html[data-theme=dark] .ztext a.ztext-link:hover{	/*链接悬停效果*/
  color: #d4d4d4;
  border-bottom: 1px solid #d4d4d4 !important;
  transition: .1s ease-out;
}
.ztext a.external:hover>.invisible, .ztext a.internal:hover>.invisible, .ztext a.ztext-link:hover>.invisible{
  color: #d4d4d4 !important;
  border-bottom: .5px solid #d4d4d4 !important;
  transition: .1s ease-out;
}
html[data-theme=dark] .ztext a.external:active, html[data-theme=dark] .ztext a.internal:active, html[data-theme=dark] .ztext a.ztext-link:active{	/*链接按住效果*/
  color: #6a6a6a;
  border-bottom: .5px solid #6a6a6a !important;
  transition: .03s ease-out;
}
.ztext a.external:active>.invisible, .ztext a.internal:active>.invisible, .ztext a.ztext-link:active>.invisible{
  color: #6a6a6a !important;
  border-bottom: .5px solid #6a6a6a !important;
  transition: .03s ease-out;
}
/*评论区连接*/
.css-cgehm8 a.internal, .css-cgehm8 a.external,.css-cgehm8 a.internal>.invisible, .css-cgehm8 a.external>.invisible{
  color: rgb(212, 212, 212) !important;
}
.css-cgehm8 a.internal:hover, .css-cgehm8 a.external:hover, .css-cgehm8 a.internal:hover>.invisible, .css-cgehm8 a.external:hover>.invisible{
  color: #b4b4b4 !important;
  border-bottom: .5px solid #b4b4b4 !important;
  transition: .1s ease-out;
}
.css-cgehm8 a.internal:active, .css-cgehm8 a.external:active, .css-cgehm8 a.internal:active>.invisible, .css-cgehm8 a.external:active>.invisible{
  color: #5a5a5a !important;
  border-bottom: .5px solid #5a5a5a !important;
  transition: .03s ease-out;
}
/*------------滚动条样式------------*/
::-webkit-scrollbar{	/*宽度*/
	width: 5px;
  height: 8px;
}
::-webkit-scrollbar-track{	/*背景*/
	background-color: #30303040;
}
::-webkit-scrollbar-thumb{	/*滑块*/
	background-color: #606060;
  border-radius: 1000px;
}
/*------------其他处理------------*/
/*为便于截图，左移问题详情版块*/
.QuestionHeader{
  transform: translateX(-14px);
}
/*首页关注和推荐的标题，和用户页“关注了/关注者”名单相同处理*/
.ContentItem-title a:hover, html[data-theme=dark] .NumberBoard-item.Button:hover .NumberBoard-itemName, html[data-theme=dark] .NumberBoard-item.Button:hover .NumberBoard-itemValue{
  color: #d4d4d4 !important;
  transition: .08s ease-in;
}
.ContentItem-title a:active, html[data-theme=dark] .NumberBoard-item.Button:active .NumberBoard-itemName, html[data-theme=dark] .NumberBoard-item.Button:active .NumberBoard-itemValue{
  color: #a0a0a0 !important;
  transition: .03s ease-in;
}
/*首页关注和推荐的折叠内容*/
.RichContent-inner:hover{
  transition: .08s ease-in;
}
.RichContent-inner:active{
  color: white !important;
  transition: .03s ease-in;
}
/*首页“关注/推荐/热榜/视频”按钮*/
html[data-theme=dark] .TopstoryTabs-link:hover{
  color: #d4d4d4 !important;
  transition: .08s ease-in;
}
html[data-theme=dark] .TopstoryTabs-link:active{
  color: #a0a0a0 !important;
  transition: .03s ease-in;
}
.TopstoryTabs-link.is-active{	/*当前选中的项*/
  font-weight: bold;
  color: white !important;
}
/*链接卡片让链接显示完全*/
.LinkCard-desc{
  overflow: visible !important;
}
}
"}
