style.css:
@-moz-document domain(twitter.com), domain(x.com) {
	article a[href^="https://t.co"][aria-label] > div:last-child {
  	display: none;
  }

  article a[href^="https://t.co"][aria-label] {
    --linkname: attr(aria-label);
  }

  article a[href^="https://t.co"][aria-label]::before {
  	content: var(--linkname);
  	position: absolute;
  	display: block;
  	bottom: 0;
  	padding: 6px;
  	margin: 12px;
  	z-index: 1;
  	color: #fff;
  	font-family: sans-serif;
  	background: rgba(0, 0, 0, .3);
  	border-radius: 4px;
  	backdrop-filter: blur(3px);
  	transition: opacity .12s ease-in-out .5s;
  }


	article a[href^="https://t.co"][aria-label]:hover::before {
  	opacity: 0;
  	transition: opacity .12s ease-in-out 3s;
	}

}
