style.css:
@-moz-document domain('smotret-anime.com') {
	@media (max-width: 990px) {
    .m-translation-view > .row > .s12.m8.l9 {
        width: 100%;
    }
    
    .m-translation-view > .row > .s12.m4.l3 {
        width: 40vw;
        position: fixed;
        top: 0;
        right: 0;
        height: 100vh;
        overflow-y: auto;
        overflow-x: visible !important;
        transform: translateX(93%);
        z-index: 5000;
        transition: transform 0.3s ease;
    }
    .m-translation-view > .row > .s12.m4.l3:hover {
        transform: translateX(0);
    }
}
}
