style.css:
body {
	background-color: #333;
	color: #ccc;
	text-transform: uppercase;
	font-size: 30px;
	text-align: center;
	padding-top: 10%;
	font-family: Arial, Helvetica;
	font-weight: 500;
	animation: fadeIn 1s;
  	opacity: 1;
}

body::before {
  content: "Connection ";
}


@keyframes fadeIn {
  0% {
    opacity: 0;
    /* transform: scale(0, 0); */
	background-color: #fff;
  }

  100% {
    opacity: 1;
    /* transform: scale(1, 1); */
    background-color: #333;
  }
}
