style.css:
@-moz-document domain("filelist.io") {  
  
html {
       background: url('https://i.postimg.cc/zfpZBz9P/File-List-Tema-Halloween.png') no-repeat center center fixed;
       -webkit-background-size: cover;
       -moz-background-size: cover;
       -o-background-size: cover;
       background-size: cover;
   }
   body {
       background: none;
   }
   .wrapper {
       background: none;
   }
   .mainheader {
       background: none;
   }
   .nblock-content a {
       color: #39cd2a;
   }
   #navigation {
        background-image: linear-gradient(#3d1f65, #271348);
        border: none;
        box-shadow: none;
        padding: 10px 10px 10px 10px;
   }
   #navigation li a {
        color: #f06816!important;
        text-shadow: 1px 1px 1px #111;
   }
   #navigation li a:hover {
        color: #f06816!important;
        text-shadow: 1px 1px 1px #111;
   }
   #nav {
        height: auto;
        padding: 6px 20px;
        font-size: 13px;
        font-weight: bold;
        justify-content: center;
        align-items: center;
        display: flex;
   }
   .torrenttable {
        float: left;
        padding: 2px 0px;
        border: none;
        border-bottom: solid 1px rgba(253, 254, 255, 0.01);
   }
   .torrenttable img {
        border-radius: 9px;
   }
   .torrenttable a {
        color: #f06816!important;
        text-shadow: 1px 1px 1px #111;
   }
   .cblock-innercontent table form {
        padding: 10px -5px 10px 10px!important;
   }
   .noborder input,select {
         background: #222;
         border: 1px solid #5f35b2;
         padding: 10px 1px 10px 10px;
         color: #fff;
   }
   .noborder input:hover,select:hover {
         background: #222;
         border: 1px solid #5f35b2;
         padding: 10px 1px 10px 10px;
         color: #fff;
   }
    .logo {
         width: 400px;
         height: 180px;
         position: relative;
         top: -10px;
         left: 80px;
   }
    .statusbar {
          border: 1px solid #2c174f;
          background: rgba(44, 23, 79, 0.8);
          text-align: center;
   }
    .statusbar a {
         color: #f06816;
   }
    #container a {
         color: #f06816;
   }
    .cblock-top, .cblock-bottom {
          display: none;
   }
    .cblock-content {
          border-left: 1px solid #4f2b8d;  
          border-right: 1px solid #4f2b8d;
   }
    .cblock-content, .cblock-innercontent {
          background: rgba(44, 23, 79, 0.8);
   }
    .nblock-content {
          background: rgba(44, 23, 79, 0.3);  
   }
    .sblock-content {
          background: rgba(44, 23, 79, 0.8);  
   }
    .colhead {
          background: rgba(44, 23, 79, 0.8);
          border: 1px solid #6c39ae;
   }
    .nblock-headerleft, .nblock-header, .nblock-headerright {
          background: rgba(44, 23, 79, 0.9);    
   }
    .sblock-headerleft, .sblock-header, .sblock-headerright {
          background: rgba(44, 23, 79, 0.9);    
   }
    .cblock-headerleft, .cblock-header, .cblock-headerright {
          background: rgba(44, 23, 79, 0.9);    
   }
    .colhead {
           background: rgba(44, 23, 79, 0.9);    
   }
     option {
           color: white!important;
   }
     input.btn {
       padding: 5px 10px 15px 10px!important;    
   }
     .logo {
            position: absolute;
            top: -0px;
            background: transparent url('https://i.postimg.cc/gJHTpX9F/File-List-Logo.png') no-repeat;
            margin-top: 5px;
            max-height: 130px;
            min-width: 250px;
            padding-bottom: 20px;
   }
     ::-webkit-scrollbar {
            width: 1.4em;
   }
     ::-webkit-scrollbar-track {
            background: transparent;
   }
     ::-webkit-scrollbar-thumb {
            background-color: #271348;
   }
     #poll_separator {
            margin-top: 5px;
            padding-top: 5px;
            border-top: 1px solid rgba(237, 248, 255, 0.01);
   }
     .mainheader {
            width: 100%;
            height: 245px;
            min-width: 1000px;
   }
     .status_avatar {
           margin:6px 41px 6px 6px;
           border: 1px solid;
           border-color: #5f35b;
           width:55px;
           height:65px;
   }
     body {
           letter-spacing: 0.2px;
           line-height: 13px;
   }
     .cblock-content {
           width: 978px;
           padding: 10px;
           background: rgba(0, 0, 0, 0.14);
           position: relative;
           border-radius: 10px;
   }
      select option {
           color: #fff !important;
   }
     .colhead {
           font-weight: bold;
           color: #E0E0E0;
           background: #151a1d;
           border: 1px solid #444;
           padding: 5px;
   }
     div#footer {
           background-color: rgba(44, 23, 79, 0.8);
   }
    div#copyright{
           background-color: rgba(44, 23, 79, 0.8);
   }
    hr.separator {
           border: 0;
           height: 3px;
           width: 600px;
           margin: 11px auto 0px auto;
           background-image: -webkit-linear-gradient(left, rgba(0,0,0,0), rgba(0,0,0,0.75), rgba(0,0,0,0));
   }
   #poll_separator {
          margin-top: 5px;
          padding-top: 5px;
          border-top: 1px solid #444;
   }
    .small {
        font-size: 7.5pt;
   }
}
