style.css:
@-moz-document domain("o2switch.net") {
html,video, img,image,svg {
    filter: invert(1) hue-rotate(120deg) !important;
/* 	background-color:black; */
}
.html5-video-container {
    filter: invert(0) hue-rotate(180deg) !important;
	background-color:white;
}
/* Titre sous TOPBAR */
.page-header {
    padding-bottom: 6px;
    margin: 6px 0 8px;
}
/* SEARCH BOX */
.header {
    background: #ffffff4d !important;
    height: 40px !important;
}
/* DIV */ 
div#boxes {
  display: inline-flex !important;
}
div[id^="container"] {
    max-width: 40 !important;
}
/* BODY TXT COLORS */
nav {
background: #ffffff;
}
body[data-app-key="tools"] {
    color: #104800;
}
#item_directory_privacy > span.itemTextWrapper.link {
    color: #104800;
}
/* Taille TXT items */
span.itemTextWrapper.link {
  font-size: 12px;
}
/* TITRES SECTIONS */
.cp-layout-main-content--with-header {
    padding-top: 0px;
}
body[data-app-key="tools"] #boxes .icon-container-body {
    display: unset;
}
body[data-app-key="tools"] #boxes .panel-heading {
  background-image: url(data:image/png;base64,);
	background-clip: border-box;
	background-origin: padding-box;
	background-attachment: scroll;
	background-repeat: repeat;
	background-size: auto;
	background-position: left top;
  border-radius: px;
   filter: invert(1) hue-rotate(180deg) !important;
}
body[data-app-key="tools"] #boxes .panel-heading {
    color: darkgray;
}
/* Cacher les favoris O2switch (doublons!) */
#o2switch-group {
  display: none;
}

#databases-header:before {
  content: "[ MySQL ] ";
  color:darkgreen;
       font-family: Roboto;
    font-weight: bold;
    font-size: 1em;
}
#files-header:before {
  content: "[ FTP ] ";
  color:darkgreen;
       font-family: Roboto;
    font-weight: bold;
    font-size: 1em;
}
#domains-header:before {
  content: "-+*[ VHOST ]*+- ";
  color:red;
     font-family: Roboto;
    font-weight: bold;
    font-size: 1em;
}
#outils-header:before {
  content: "-+*[ LUNES ]*+- ";
  color:red;
     font-family: Roboto;
    font-weight: bold;
    font-size: 1em;
}
#outils-header:after {
  content: " & SSH";
  color:red;
     font-family: Roboto;
    font-weight: bold;
    font-size: 1em;
}
#item_o2switch-multiple-accounts > span.itemTextWrapper.link:before {
  content: "-+[ LUNES ]+- ";
    color:red;
      font-weight: bold;
}
#performance-header:before {
  content: "[ CACHE ] ";
  color:darkcyan;
     font-family: Roboto;
    font-weight: bold;
    font-size: 1em;
}
#security-header:before {
  content: "[ SSL ] ";
  color:darkcyan;
     font-family: Roboto;
    font-weight: bold;
    font-size: 1em;
}
#software-header:before {
  content: "[ PHP Version ] ";
  color:darkcyan;
     font-family: Roboto;
    font-weight: bold;
    font-size: 1em;
}
#software-header:before {
  content: "[ PHP Version ] ";
  color:darkgreen;
       font-family: Roboto;
    font-weight: bold;
    font-size: 1em;
}
#advanced-header:before {
  content: "[ CRON ] ";
  color:darkgreen;
       font-family: Roboto;
    font-weight: bold;
    font-size: 1em;
}
#advanced-header:after {
  content: " & TERMINAL";
  color:darkgreen;
       font-family: Roboto;
    font-weight: bold;
    font-size: 1em;
}
div #sitepad_div-container {
  display: none;
}
/* colonne NOM DE LA LUNE */
div #generalInfoSection {
position: sticky;
  top: 0;
}
/* largeur des colonnes */
.col-lg-9 {
  width: 85% !important;
}
.col-lg-3 {
  width: 15% !important;
}
/* Tetes des colonnes */
body[data-app-key="tools"] #boxes .panel {
  border-radius: 44px !important;
}
body[data-app-key="tools"] #boxes .panel-heading {
  padding: 5px !important;
}
#ddlThemes_chosen .chosen-single, #ddlAccounts_chosen .chosen-single, #txtUserName {
    border: 3px solid #104800;
    background-image: none;
    background-color: #f7f9fc;
    color: #ff0088;
    font-weight: bold;
    border-radius:16px;
}
#txtHomeDirectory {
      border: 3px solid #104800;
    background-image: none;
    background-color: #f7f9fc;
    color: #ff0088;
    font-weight: bold;
  border-radius:12px;
}
aside#cp-main-menu-container{
    filter: invert(1) hue-rotate(180deg) !important;
    position: fixed !important;
    right: 0px !important;
    z-index: 9999 !important;
    width: 100% !important;
    top: 0px !important;
    left: 0px !important;
    max-width: none !important;
    min-width: auto !important;
    height: 68px !important;
  right: 0px !important;
  overflow: initial !important;
  padding-left: 40px !important;
}
aside#cp-main-menu-container > cp-main-menu{
    height: 68px !important;
    display: block !important;
    position: absolute !important;
    width: 100% !important;
    top: 0 !important;
    left: 0px !important;
    max-width: 280px;
  overflow: hidden !important;
}
aside#cp-main-menu-container > cp-main-menu:hover,aside#cp-main-menu-container > cp-main-menu:active,aside#cp-main-menu-container > cp-main-menu:focus{
	height: 160px !important;
  overflow: visible !important;
}
.cp-layout-main-menu{
  background-color: white !important;
}
.cp-layout-wrapper{
  margin-left: 0px !important;
}
.container-lg{
  max-width: none !important;
}
.cp-layout-page{
  padding-top: 100px !important;
}
.cp-layout-header, cp-header {
    filter: invert(1) hue-rotate(0deg) !important;
  position: fixed !important;
  height: 68px !important;
  right: 0px !important;
  z-index: 999999 !important;
  top: 0px !important;
  background-color: transparent !important;
      padding-right: 20px;
    padding-left: 20px;
  width: 400px !important;
}
.header__search-wrapper, .header__search-input {
    background: #cfd1ffa8;
}
.cp-layout-main-menu {
    background-color: #050505 !important;
  	background-image: url(data:image/png;base64,);
	background-clip: border-box;
	background-origin: padding-box;
	background-attachment: scroll;
	background-repeat: repeat;
	background-size: auto;
	background-position: left top;
}
/* Table fond transparent */
.table-striped tbody tr:nth-of-type(odd) {
    background-color: #f1f1f1ba;
}
.table-striped tbody tr:nth-of-type(even) {
    background-color: #f8f8f8d9;
}
/* Fleches accordeons */
body[data-app-key="tools"] #boxes .panel-heading {
  display: inline-block;
}
.cp-layout-page {
    padding-top: 70px !important;
}
/* Search Box */
.header {
  background: transparent !important;
}
/* Correction PHPMyadmin */
.breadcrumb-navbar {
    text-shadow: unset;
    }
}

