style.css:
@-moz-document domain("pinboard.in")
{
	* {
		font-family: "Arial Rounded MT Bold", Arial, Helvetica, sans-serif;
	}
	body {
		background: #000 !important;
		font-size: 20px;
		color: #fc9;
	}
	#content {
		width: 98vw;
	}
	#banner {
		background: #606;
		max-width: initial;
		width: 98.25vw;
		padding: 0.5rem;
		margin: 0 0 0 -1rem;
		border-bottom: 3px double #f0f;
	}
	.pin_logo {
		margin-right: 2px;
		opacity: 1;
		height: 20px;
		background: #fc9;
		border-radius: 1rem;
	}
	a#pinboard_name,
	div#bmarks_page_nav a.filter {
		color: #fc9;
	}
	a.banner_username, a.banner_username:visited {
		color: #f9f;
	}
	div.small_username a, div.small_username:visited {
		color: #c93;
		font-style: italic;
	}
	div#main_column {
		width: 60vw;
		max-width: fit-content;
	}
	div#right_bar {
		width: 37vw;
	}
	div#bmarks_page_nav {
		color: #909;
	}
	div#bookmarks {
		background: #000;
		color: #fc9;
	}
	.bookmark_count_box {
		width: auto;
	}
	.bookmark {
		padding: 1rem;
	}
	.bookmark_title {
		color: #6cf;
	}
	.star {
		margin-left: -22px;
		color: #ffc;
	}
	a.url_display {
		color: #fc9 !important;
		text-decoration-style: solid;
		text-decoration-line: underline;
		text-decoration-thickness: 3px;
		text-decoration-color: #09f;
	}
	div.description {
		color: #fff;
	}
	a.tag {
		background: #fc9;
		color: #000;
		padding: 3px 4px 3px 1rem;
		border-radius: 1rem 2px 2px 1rem;
	}
	.private {
		border: 0;
		border-radius: 1rem;
		background: #66f7;
		color: #fc9;
	}
	.private a {
		/* color: #0cf; */
	}
	.when {
		color: #ff3;
	}
	a:visited {
		color: #0cc;
	}
	a.unread {
		color: #036;
		text-shadow: -1px -1px 3px #9cf, 1px -1px 3px #9cf, 1px 1px 3px #9cf, -1px 1px 3px #9cf;
	}
	a.unread:after {
		content: " (unread)";
		font-size: smaller;
	}
	#tag_cloud {
		width: 100%;
	}
	div.tag_table table {
		width: 100%;
	}
	div.tag_table table tbody {
		display: flex;
		flex-wrap: wrap;
		flex-direction: row;
		max-width: 30vw;
		align-content: center;
		justify-content: space-evenly;
		align-items: center;
	}
	div.tag_table table tbody tr {
		display: 1 0 auto;
	}
	div.tag_table table tbody tr td[width] {
		width: auto;
		padding: 0.25rem;
	}
	input#search_query_field {
		background: transparent;
		border-radius: 0.5rem;
		padding: 0.5rem;
		color: #fc9;
		border: 1px solid #fc9;
		margin: 1rem 0;
	}
	div#top_menu a, div#top_menu a:visited {
		color: #0ff;
	}
}
