style.css:
/* Set a red background color for the entire Discord app */
body {
  background-color: red;
}

/* Change text and icon colors to white */
div, span, a, p, h1, h2, h3, h4, h5, h6, i, img, svg, button, label {
  color: white !important;
}

/* Adjust some elements' background colors */
/* For example, change the server list background */
div[class*="containerDefault"]:not([class*="hidden"]):not([class*="background"]) {
  background-color: darkred !important;
}

/* Customize server icons and channels */
/* For example, change the server icon background color to a darker red */
div[class*="sidebar"]:not([class*="hidden"]) div[class*="guild"]:not([class*="banner"]) {
  background-color: #AA0000 !important;
}

/* Change the text color in the server list */
div[class*="sidebar"] span[class*="serverName-"] {
  color: white !important;
}

/* Customize message background and text */
div[class*="message-"] {
  background-color: #FF0000 !important;
  color: white !important;
}

/* Modify user statuses (online, idle, etc.) */
div[class*="member-"] div[class*="status-"] {
  background-color: #AA0000 !important;
}

/* Customize the color of links in messages */
div[class*="markup-"] a {
  color: #FF5555 !important;
}

/* Customize server and channel names */
div[class*="containerDefault"]:not([class*="hidden"]) span {
  color: white !important;
}

/* Adjust background and text color of the input field */
div[class*="input"]:not([class*="hidden"]) {
  background-color: #FF0000 !important;
  color: white !important;
}
