style.css:
@-moz-document domain("filelist.io") {
html {
    background: url('https://i.postimg.cc/3wrp8SCB/Black-Friday.png') no-repeat center center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
   }
   body {
    background: none;
   }
   a {
    color: #555555;
   }
   .wrapper {
     background: none;
   }
   .mainheader {
     background: none;
   }
   .nblock-content a {
     color: #555555;
   }
   #navigation {
    background-color: #1e1e1e;
    border: 1px solid #232323;
     justify-content: center;
    padding-top: 10px;
    padding-bottom: 10px;
    padding-left: 15px;
    padding-right: 10px;
    margin: 5px;
    top: 170px;
    width: 962px;
    height: 44px;
    display: flex;
   }
   #navigation li a {
    color: #848482!important;
    text-shadow: 1px 1px 1px #111;
   }
   #navigation li a:hover {
    color: #848482!important;
    text-shadow: 1px 1px 1px #111;
   }
  .torrenttable {
    float: left;
    padding: 2px 0px;
    border: none;
    border-bottom: solid 1px rgba(253, 254, 255, 0.01);
   }
  .torrenttable img {
    border-radius: 9px;
   }
  .torrenttable a {
     color: #848482!important;
     text-shadow: 1px 1px 1px #111;
   }
   .cblock-innercontent table form {
     padding: 10px -5px 10px 10px!important;
   }
   .statusbar {
     border: 1px solid #232323;
     background-color: #1e1e1e;
     text-align: center;
   }
   .statusbar a {
     color: #848482;
   }
   #container a {
     color: #008000;
   }
  .cblock-content {
     width: 952px;
     padding: 7px;
     background: rgba(0, 0, 0, 0.14);
     position: relative;
     border-radius: 10px;
     border-left: 4px solid #1c1c1c;
     border-right: 4px solid #1c1c1c;
     border-top: 4px solid #1c1c1c;
     border-bottom: 4px solid #1c1c1c;
    }
    .cblock-headerleft, .cblock-header, .cblock-headerright {
     position: relative;
     bottom: 0px;
   }
   .cblock-content, .cblock-innercontent {
     background: rgba(19, 19, 19, 1);
   }
  .logo {
     position: absolute;
     top: +20px;
     background: transparent url('https://i.postimg.cc/28L7k1m3/File-List-Logo.png') no-repeat;
     margin-top: 5px;
     max-height: 130px;
     min-width: 250px;
     padding-bottom: 20px;
      width: 400px;
     height: 180px;
      left: 80px;
     }
   ::-webkit-scrollbar {
    width: 1.4em;
  }
   ::-webkit-scrollbar-track {
    background: #848482;
  }
   ::-webkit-scrollbar-thumb {
    background-color: #1e1e1e;
  }
   #poll_separator {
    margin-top: 5px;
    padding-top: 5px;
    border-top: 1px solid rgba(237, 248, 255, 0.01);
  }
  .mainheader {
    width: 99%;
    height: 245px;
    min-width: 1000px;
  }
  .status_avatar {
    margin:6px 41px 6px 6px;
    border: 2px solid;
    border-color: #848482;
    width:55px;
    height:65px;
  }
  select option {
    color: #fff !important;
  }
  select {
    background: #848482;
  }
  .colhead {
    font-weight: bold;
    color: #848482;
    background-color: #131313;
    border: 1px solid #444;
    padding: 5px;
    margin-left: 0px;
  }
   td.colhead {
    margin-left: 8px; 
  }
   div#footer {
    background-color: #1e1e1e;
  }
   div#copyright {
    background-color: #0C090A;
  }
   #copyrightInner {
    width: 970px;
    margin: 0 auto;
    font-size: 11px;
    color: #db0f0;
    padding: 13px 0;
    position: relative;
  }
   .nblock-innercontent {
    position: relative;
    bottom: 2px;
    top: -8px;
  }
   .nblock-bottom {
    width: 683px;
    height: 6px;
    display: block;
    background: #222; 
    margin-bottom: 20px;
  }
   .sblock-bottom {
    width: 272px;
    height: 100%;
    background-color: #131313;
    border: 3px solid #252525;
  }
   #maincolumn {
    width: 986px;
    min-height: 400px;
    margin: 0 auto;
  }
  .subheader {
    width: 995px;
    height: 150px;
    margin: 0 auto;
    position: relative;
  }
    td.colhead {
    font-size: 7,5pt;
  }
   #sectionmain {
    width: 95%;
    margin: 15px 0px 0px 17px;
  }
  #forummain {
    width: 95%;
    margin: 0 auto;
    margin-top: 0px;
    margin-right: auto;
    margin-bottom: 0px;
    margin-left: 17px;
  }
  hr.separator {
    border: 0;
    height: 3px;
    width: 600px;
    margin: 11px auto 0px auto;
    background-image: -webkit-linear-gradient(left, rgba(0,0,0,0), rgba(0,0,0,0.75), rgba(0,0,0,0));
  }
   #poll_separator {
     margin-top: 5px;
     padding-top: 5px;
     border-top: 1px solid #444;
  }
   .small {
      font-size: 7.5pt;
  }
   h1 {
          font-size: 12pt;
          color:  #848482;
   }
     textarea {
         font-size: 8pt;
         background: #C0C0C0;
         border: 1px solid #9ACD32;
         max-width: 650px !important;
         max-height: 300px !important;
         border-radius: 3px;
   }
   input {
        background: #C0C0C0;
        padding: 5px 4px;
        border: 1px solid #e2e2e2;
        border-radius: 3px;
   }
  .visitedlinks a:visited {
       text-decoration: none;
       color: #CD5C5C !important;
  }
 .barmax {
    border: 1px solid #267191;
    background: #CD5C5C;
  }
    .pager a, .pager a:link {
        margin: 0 2px;
        padding: 4px 6px;
        background: #4C4646;
        border: 1px solid #E2E2E2;
        text-decoration: none;
        font-weight: normal;
        font-size: 8pt;
        color: #666;
        border-radius: 3px;
  }
   .nblock-header h4 {
   color: #848482;
   }
  .sblock-header h4 {
   color: #848482;
   }
  #wrapper {
   color: #848482;
   }
}
