style.css:
@-moz-document url-prefix("https://trello.com/")
{
  #board {
    overflow-x: hidden;
    overflow-y: auto;
    display: flex;
    align-content: flex-start;
    flex-direction: row;
    flex-wrap: wrap;
  }

  #board li[data-testid="list-wrapper"],
  #board div[data-testid="list-composer-button-container"] {
    margin: 0 0 10px 10px;
    max-height: 40vh;
  }
}
