style.css:
@-moz-document domain(youtube.com) {

h1.ytd-watch-metadata{
  font-weight: 500
}
ytd-watch-flexy #info, ytd-structured-description-content-renderer.ytd-watch-metadata{
  display: none
}

ytd-c4-tabbed-header-renderer[use-page-header-style] #channel-name.ytd-c4-tabbed-header-renderer{
  font-size: 2.6rem;
  font-weight: 400;
}

ytd-c4-tabbed-header-renderer[use-page-header-style] .page-header-banner.ytd-c4-tabbed-header-renderer{
	border-radius: 0;
}


yt-smartimation.ytd-subscribe-button-renderer{
  border : 1px solid #ee0027 !important;
}

yt-smartimation.ytd-subscribe-button-renderer .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--tonal{
  color: #ee0027
}

#description.ytd-watch-metadata{
  background: #0a0a0a;
    border-radius: 0
}

#description.ytd-watch-metadata:hover{
  background: #0a0a0a !important;
}

#info-container.ytd-watch-info-text {

  border-bottom: 1px solid var(--yt-spec-10-percent-layer);
  padding-bottom: 10px
}

.yt-spec-button-shape-next--mono.yt-spec-button-shape-next--filled, .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--filled:hover{
  background: #ee0027
}


#container.ytd-searchbox, #search-icon-legacy.ytd-searchbox{
  border-radius: 0
}







#top-row.ytd-watch-metadata{
  border-bottom: none !important;
  margin-top: 10px
}

ytd-watch-info-text:not([detailed]) #info.ytd-watch-info-text,  ytd-watch-info-text[detailed] #info.ytd-watch-info-text{

  display: block
}

ytd-text-inline-expander{
  margin-top: 10px
}

    #meta-contents[hidden],
