style.css:
@-moz-document domain("dreambubble.xyz") {
    /* Original website created by Hex 
    You can find them on github here: https://github.com/hecksadecimal
    */
    body {
        background-color: #080808 !important;
        color: #fafafa;
        font-weight: 600;
    }

   .image { /*Site logo*/
        width: 511px;
        background: url("https://i.imgur.com/7SE4Ccp.gif") 0 bottom no-repeat !important;
        background-size: contain;
        border-radius:10px;
    }

    #navbar {
        border-radius:0px 0px 6px 6px;
        background-color: #181818;
    }
    
    #nav { /*Navigation bar content*/
        border: 0px solid transparent;
        text-transform: uppercase !important;
        font-weight: 800;
        padding-left: 0px;
        background-color: #181818;
        border-radius:0px 0px 6px 6px;

    }
    
    #nav li { /*Zoomed-in menu appearance*/
        background-color: transparent;
    }
    
    .menubutton {
        background:none;
        padding-bottom:0px;
        margin-bottom:12px;
    }
    
    #nav.is-open { /*Zoomed-in open menu appearance*/
        background-color: #080808;
        margin-top: -23px;
        border-radius:0px 0px 6px 6px;
    }
    
    #charbar { /*Character bar*/
        background-color: #181818;
    }
    
    audio { /*Resize and move audio player*/
        width: 100%;
        height: 40px;
        margin-top: 10%;
    }
    
    h1 {
        color: #fafafa !important;
    }

    a:link { /*Non visited links*/
        color: #24d0b8;
        font-weight: 700;
        text-decoration: underline !important;
    }

    a:visited { /*Visited links*/
        color: #6438AC;
    }
    
    a:hover { /*hover link links*/
        color: #44FFD8;
    }
    
    p { /*All text*/
        margin: 1px 1px !important;
        padding: 1px 1px !important;
    }
    
    option { /*Character picker options*/
        color: #fafafa !important;
        font-weight: 300 !important;
    }
    
    optgroup { /*Character picker categories*/
        color: #24d0b8 !important;
        font-weight: 300 !important;
    }
    
    input {
        border: 0px solid;
        background-color: #080808 !important;
        border-radius: 5px !important;
        color: #fafafa;
    }

    select {
        border: 3px solid #383838;
        background-color: #282828 !important;
        border-radius: 3px;
        color: #fafafa;
    }

    .picky-header {
        color: #fafafa !important;
    }

    #serverstatus
        .ok {
            color: #18A818 !important;
        }
        .warn {
            color: #EC9C2C !important;
        }
        .down {
            color: #F01818 !important;
        }

    button {
        background-color: #fafafa !important;
        color: #080808 !important;
        border-radius: 3px;
        border: 0px solid;
    }
    
    .text { /*all text, but mostly messages*/
        border-radius: 2px;
        padding:2px;
        display:block;
    }
    
    .pester-line { /*Home page quirk preview*/
        background-color: #fafafa;
        border-radius: 5px;
        padding-left: 5px !important;
        padding-right: 5px !important;

    }
    .linkButton {
        background-color: #fafafa !important;
        color: #080808 !important;
        border-radius: 3px !important;
    }

    #conversation { /*Conversation box*/
        border: 0px solid;
        background-color: #282828 !important;
        border-radius: 6px;
    }

    .search { /*New search Icon*/
        background-image: url("https://i.imgur.com/XDCEgOU.gif") !important;
        background-size: 128px 128px !important;
    }

    #userList { /*userlist box*/
        border: 0px solid;
        background-color: #282828 !important;
        border-radius: 6px;
    }
    
    .system.message {
        color: #fafafa !important;
    }

    .states {
        color: #fafafa !important;
    }

    .userID {
        color: #fafafa !important;
    }

    .sidebar ul ul {
        color: #fff;
    }

    #container {
        background-color: #282828 !important;
        border-radius:16px;
    }

    #reportLink { /*Report button is treated differently*/
        background-color: #fafafa !important;
        color: #080808 !important;
        border-radius: 3px;
        border: 0px solid;
        text-decoration: none !important;
    }

    #logLink { /*Log button is treated differently*/
        background-color: #fafafa !important;
        color: #080808 !important;
        border-radius: 3px;
        border: 0px solid;
        text-decoration: none !important;

    }

    #preview { /*Message preview with quirk*/
        border: 0px solid #2c2c2c;
        background-color: #282828 !important;
        border-radius: 5px;
        margin-top: 1px !important;
    }

    #hidePreview {
        background-color: #2c2c2c;
        border-radius: 5px;
        padding: 2px;
        font-weight:700;
    }

    #textInput { /*Message input box*/
        color: #fafafa;
        border: 2px solid #2c2c2c !important;
        border-bottom-right-radius: 5px !important;
        border-bottom-left-radius: 5px !important;
        margin-top: 6px !important;
    }

    button[type=submit] { /*Larger Send button*/
        display: inline-block;
        width: 29% !important;
    }

    #settings {
        border: 0px solid;
        background-color: #181818 !important;
        border-radius: 6px;
    }
    
    .colorpicker { /*Tweaked color picker appearance*/
        border: 0px solid #fff !important;
        background: url("https://i.imgur.com/wWXoY43.png");
    }

    .colorpicker input{ /*tweaked text boxes in the color picker*/
        width: 31px;
        font-size: 10px;
        border-radius:0px !important;
		}
}
