style.css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
  .ps-room {
	position: absolute;
	display: block;
	top: 56px;
	left: 0;
	right: 0;
	bottom: 0;

	border-left: 1px solid #AAAAAA;
	margin-left: -1px;
}
.scrollable {
	overflow: auto;
	-webkit-overflow-scrolling: touch;
	overflow-scrolling: touch;
}
.ps-room.ps-room-light {
	background: rgba(242,247,250,.85);
	color: black;
}
.ps-room.ps-room-opaque {
	background: #EEF2F5;
	color: black;
}

.ps-overlay {
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	background: #CCCCCC;
	background: rgba(212, 212, 212, .5);

	overflow: auto;
	-webkit-overflow-scrolling: touch;
	overflow-scrolling: touch;
	z-index: 20;
}
.ps-popup {
	position: absolute;
	top: auto; right: auto; left: auto; bottom: auto;
	text-align: left;
	background: #E1E8E8;
	color: black;
	border: 1px solid #999999;
	padding: 2px 10px;

	border-radius: 6px;
	box-shadow: inset 1px 1px 0 #ffffff, inset -1px -1px 0 #DDDDDD, 2px 2px 3px rgba(0,0,0,.2);
	z-index: 20;
}
.ps-overlay .ps-popup {
	position: static;
	margin: 80px auto 20px auto;
	max-width: 320px;
}
.ps-popup p,
.ps-popup h3 {
	margin: 7px 0;
}
.ps-popup h3 {
	font-size: 12pt;
}
p.or {
	font-size: 12pt;
	font-style: italic;
	font-weight: bold;
	color: #444444;
	margin: 20px 0;
	text-align: center;
}
p.or:before,
p.or:after {
	content: "";
	font-size: 10px;
	padding: 0 0 0 85px;
	margin: 0 9px 0 0;
	border-bottom: 1px solid #777777;
	position: relative;
	top: -7px;
}
p.or:after {
	margin: 0 0 0 9px;
}
.ps-popup p.error {
	font-weight: bold;
	color: #EE4422;
}

.popupmenu, popupmenu li {
	margin: -2px 0 -1px 0;
	padding: 0;
	list-style: none;
	font-size: 8pt;
}
.popupmenu h3 {
	margin: 5px 0 0 -2px;
	padding: 1px 0;
	font-size: 8pt;
	color: #579;
}
.popupmenu li:first-child h3 {
	margin-top: 0;
}
.popupmenu button {
	display: block;
	font-size: 8pt;
	font-family: Verdana, Helvetica, Arial, sans-serif;
	margin: 0 0 0 6px;
	padding: 2px 3px;
	border: 1px solid transparent;
	border-radius: 2px;
	background: transparent;
	color: black;
	width: 204px;
	text-align: left;

	box-sizing: border-box;
}
@media (max-height:590px) {
	.popupmenu h3 {
		margin-top: 2px;
	}
	.popupmenu, popupmenu li {
		margin: -3px 0 -2px 0;
	}
}
@media (max-height:518px) {
	.popupmenu h3 {
		margin-top: 1px;
	}
}

.popupmenu button.sel {
	border-color: #AAAAAA;
	white-space: nowrap;
	overflow: hidden;
}
.popupmenu button:hover,
.popupmenu button.sel:hover {
	border-color: #888888;
	background: #D5D5D5;
	color: black;
}
.popupmenu button.button {
	margin: 2px auto 5px;
	width: 184px;
}
.select, .team {
	width: 230px;
	height: 32px;
	padding: 0 5px;
	font-size: 9pt;
	text-align: left;
	font-family: Verdana, Helvetica, Arial, sans-serif;
	cursor: pointer;

	border-radius: 4px;
	border: 1px solid #888888;
	background: #EEEEEE;
	box-shadow: inset 0 1px 0 #FFFFFF;
	background: linear-gradient(to bottom,  #edf2f8,  #d7e3ec);
	color: black;
	box-sizing: border-box;
}
.team:focus {
	outline: 0 none;
	border: 1px solid #004488;
	box-shadow: inset 0px 1px 2px #D2D2D2, 0px 0px 5px #2266AA;
}
.select.teamselect {
	height: 50px;
	white-space: nowrap;
	overflow: hidden;
}
.teamselect strong {
	display: block;
	font-size: 8pt;
	margin-right: 15px;
	text-align: center;
}
.teamselect:disabled strong {
	color: #777777;
}
.dark .teamselect:disabled strong {
	color: #505050;
}
.teamselect small {
	margin: -1px 0 0 -3px;
	display: block;
}
.teamselect small .picon {
	margin: 0 -3px;
}
.select:hover, .team.team-hover {
	border-color: #555555;
	background: #FFFFFF;
	background: linear-gradient(to bottom,  #dce4ec,  #c6d3e0);
}
.select:active, .team:active {
	border-color: #777777;
	background: #DDDDDD;
	box-shadow: none;
	background: linear-gradient(to bottom,  #d1dce6,  #e7edf2);
}
.select:after {
	float: right;
	font-size: 14px;
	content: "\f0d7";
	font-family: FontAwesome;
	-webkit-font-smoothing: antialiased;
	color: #777777;
}
.select:disabled {
	border-color: #999999;
	background: rgba(220,220,220,.65);
	color: #555555;
	box-shadow: none;
}
.select:disabled:after {
	color: #AAAAAA;
}
.select:disabled small {
	opacity: 0.7;
}
.select.preselected,
.select:disabled.preselected {
	color: black;
}
}
