style.css:
/* i really want this to be global */

.player.waiting { background: linear-gradient(rgba(0,0,0,0), orange) !important; }
.player.winner { background: linear-gradient(rgba(0,0,0,0), lawngreen) !important; }
.player.game-winner { background: linear-gradient(rgba(0,0,0,0),lawngreen) !important; }

body {
  background: #000 !important;
  color: #fff !important;
}

html {
  background: #111 !important;
  color: #fff !important;
}

.board {
  background: url(https://raw.githubusercontent.com/gist/lexterror/3e6e490fd8ff7cb2ce5c433e41b46367/raw/4df67e70a3eb2029e03fb1b19b6265a8a635bf9c/WoodsyClassicLightTable.svg) !important;
  background-size:720px 720px !important;

}

.discards{
   background-size: 500px 500px !important;
   background-repeat:no-repeat !important;
   background-position:center !important;
  }

:root {
  --board-background: transparent !important;
  --discard-background: transparent !important;
  --discard-pause-text-color: transparent !important;
  --player-background: transparent !important;
  --windicator-outline: transparent !important;
  --windicator-player-wind-background: transparent !important;
  --player-id-background: transparent !important;
}


[tile] {
  border:0 !important;
}

.player [tile] {
box-shadow:
0px -5px 1px #aaaaaa,
0px -5px 1px rgba(16,16,16,1),
0px -5px 1px rgba(16,16,16,1),
0px -6px 2px #000000,
inset 0px 0px 2px rgba(0,0,0,0.5) !important;


}

.player {
  margin: 0 !important;
  width: 612px !important;
}

game-tile.latest {

box-shadow:

0px -5px 1px #aaaaaa,
0px -5px 1px rgba(16,16,16,1),
0px -5px 1px rgba(16,16,16,1),
0px -6px 2px #000000,
inset 0px 0px 2px rgba(0,0,0,0.5) !important;

}

game-tile.selectable.highlight {
  
     box-shadow:
     0px -5px 1px #aaaaaa,
     0px -5px 1px rgba(16,16,16,1),
     0px -5px 1px rgba(16,16,16,1),
     0px -6px 2px #000000,
     inset 0px 0px 2px rgba(0,0,0,0.5) !important;
}

.discards game-tile.selectable {
  box-shadow: 
  0px 3px 1px #aaaaaa,
  0px 3px 1px rgba(16,16,16,1),
  0px 3px 1px rgba(16,16,16,1),
  0px 4px 2px #000000,
  inset 0px 0px 2px rgba(0,0,0,0.5) !important;
}

.discards game-tile.selectable.highlight {
  box-shadow: 
  0px 3px 1px #aaaaaa,
  0px 3px 1px rgba(16,16,16,1),
  0px 3px 1px rgba(16,16,16,1),
  0px 4px 2px #000000,
  inset 0px 0px 2px rgba(0,0,0,0.5) !important;
}

.discards game-tile.selectable.highlight.suggestion {
  box-shadow: 
  0px 3px 1px #aaaaaa,
  0px 3px 1px rgba(16,16,16,1),
  0px 3px 1px rgba(16,16,16,1),
  0px 4px 2px #000000,
  inset 0px 0px 2px rgba(0,0,0,0.5) !important;
}

game-tile.discard {
  box-shadow: 
  0px 3px 1px #aaaaaa,
  0px 3px 1px rgba(16,16,16,1),
  0px 3px 1px rgba(16,16,16,1),
  0px 4px 2px #000000,
  inset 0px 0px 2px rgba(0,0,0,0.5) !important;
}
game-tile + [tile] {
  margin-left: 1px !important;
  
}

[tile][locked] {
  margin-top: -80px !important;

}

[tile][bonus] {
  margin-top: -80px !important;

  
}

[tile][winning] {
  
}

[tile][concealed] {
  margin-top: -80px !important;

  
}

.discards {

  max-width: 500px !important;
  margin-left: 45px !important;
}


