style.css:
@-moz-document domain("algumon.com") {
.opinion-box {
		visibility: hidden;
	}

	.deal-meta-info {
		visibility: hidden;
	}

	.deal-hashtags {
		visibility: hidden;
	}

	span.dot.red {
		visibility: hidden;
	}

	p:nth-child(3) {
		font-size: 20px;
	}

	.product-link {
		font-size: 30px;
	}

	.product-price {
		font-size: 30px;
	}

	.product-per-price {
		font-size: 30px;
	}

	.deal-header-p {
		font-size: 30px;
	}

	.post-group .label-time {
		font-size: 20px;
	}

	.post-group .site {
		font-size: 20px;
	}

	.product-img.pull-left {
		font-size: 30px;
	}

	.product-img .product-img-box {
		width: 150px;
		height: 150px;
	}

	.product li.left .product-body {
		margin-left: 180px;
		display: inherit;
		height: 0px;
	}

	.left.clearfix.post-li {
		height: 175px;
	}
}
