style.css:
@-moz-document domain("fanfiction.net") {
    span#bio_text {
        color: #000 !important;
        background: #ff881a;
        padding: 10px;
        border-radius: 20px;
        padding-right: 15px;
        padding-left: 15px;
        text-transform: uppercase;
    }
    .hiliteStyle {
        background-color: #ff881a;
        color: #121212;
    }
    img.round36 {
        display: none !important;
    }
    .lc-wrapper:after,
    .zmenu,
    div#top,
    body.yes-fontsmoothing,
    div#content_parent {
        background: #222 !important;
        border: none;
    }
    .lc-wrapper {
        margin-bottom: 15px;
    }
    td {
        color: #fff !important;
    }
    a.stitle {
        font-weight: bold;
        color: #ff881a !important;
        text-transform: uppercase;
    }
    .btn-primary {
        color: #0a0a0a;
        text-shadow: none !important;
        background: #ff881a !important;
        border: 2px solid #222;
        border-radius: 0px !important;
    }
    option {
        color: #fff;
        background: #161616;
        display: block;
    }
    .modal-footer {
        box-shadow: none !important;
        border-radius: 0px !important;
        background-color: #222;
        border: none !important;
    }
    .label,
    .badge,
    .badge:hover {
        background-color: #ff881a;
        color: #020202;
    }
    .nav-tabs > li > a:hover,
    .nav-tabs > li > a:focus {
        border-color: #ff881a !important;
        border-radius: 0px !important;
    }
    .nav > li > a:hover,
    .nav > li > a:focus {
        color: #000;
        background-color: #ff881a;
    }
    .modal {
        background-color: transparent;
        border-radius: 0px !important;
        border: 5px solid #292929;
    }
    .gui_success {
        color: #fff !important;
    }
    textarea:focus,
    input[type=text]:focus,
    input[type=password]:focus,
    input[type=datetime]:focus,
    input[type=datetime-local]:focus,
    input[type=date]:focus,
    input[type=month]:focus,
    input[type=time]:focus,
    input[type=week]:focus,
    input[type=number]:focus,
    input[type=email]:focus,
    input[type=url]:focus,
    input[type=search]:focus,
    input[type=tel]:focus,
    input[type=color]:focus,
    .uneditable-input:focus,
    input:focus:invalid:focus,
    textarea:focus:invalid:focus,
    select:focus:invalid:focus {
        border-color: #ff881a;
    }
    textarea,
    input[type=text],
    input[type=password],
    input[type=datetime],
    input[type=datetime-local],
    input[type=date],
    input[type=month],
    input[type=time],
    input[type=week],
    input[type=number],
    input[type=email],
    input[type=url],
    input[type=search],
    input[type=tel],
    input[type=color],
    .uneditable-input {
        background-color: #222;
        border: solid 2px #333;
        border-radius: 0px !important;
        box-shadow: none !important;
    }
    .panel_success {
        background: #222;
        padding: 10px;
        border: solid 2px #333;
        margin-bottom: 5px;
        color: #fff !important;
        text-align: left;
    }
    .modal-body {
        background: #222;
        border-radius: 0px !important;
        color: #ededed !important;
    }
    .modal-header {
        padding: 9px 15px;
        background: #222;
        border-bottom: none;
        color: #ff881a;
        text-transform: uppercase;
    }
    img {
        filter: grayscale(100%);
        opacity: 1;
    }
    .nav-tabs {
        border-bottom: 5px solid #222;
    }
    div#bio {
        border-color: #1e1e1e !important;
        background: #222 !important;
        margin-bottom: 20px;
        overflow: hidden;
        margin-top: 20px;
        padding: 25px !important;
        color: #dfdfdf;
    }
    .z-list,
    .z-list.zhover.zpointer {
        border-color: #1e1e1e !important;
        background: #222 !important;
        margin-bottom: 10px;
        padding: 15px;
        color: #dfdfdf;
    }
    .nav-tabs > .active > a,
    .nav-tabs > .active > a:hover,
    .nav-tabs > .active > a:focus {
        color: #ffffff;
        cursor: default;
        background-color: #222;
        border: 2px solid #222;
        border-radius: 0px !important;
        border-bottom-color: transparent;
    }
    .lc-left,
    .lc {
        background: transparent !important;
    }
    div#storytext {
        font-size: 15px;
        line-height: 1.25;
        text-align: left;
    }
    a.xcontrast_txt.xlight {
        text-transform: uppercase;
        color: #8ab4f8 !important;
    }
    .zui a {
        color: #a4d797;
        text-transform: uppercase;
        font-size: 12px;
    }
    body.yes-fontsmoothing,
    div#zmenu {
        background: #222;
    }
    .menulink.maxwidth {
        color: #ff881a!important;
        text-transform: uppercase;
        background: #222;
        background-size: initial;
        height: 32px;
    }
    .stitle,
    .novtitle,
    .footer-inner {
        border: none;
    }
    img.cimage {
        filter: none !important;
        margin-right: 15px !important;
        padding: 10px !important;
        border: 2px solid #222 !important;
        border-radius: 0px !important;
        background: #222 !important;
    }
    span.icon-twitter-3,
    [class^=icon-]:before,
    [class*=" icon-"]:before {
        color: #ff881a;
    }
    div#storytextp {
        background: #222;
        padding: 20px !important;
        margin-top: 45px;
        margin-bottom: 15px;
    }
    select {
        background-color: #161616;
        border: none;
        color: #f7f7f7;
    }
    .selection_hl {
        background-color: #ff881a;
        color: #000;
        border: none;
        border-radius: 0px !important;
    }
    .btn-group > .btn:first-child,
    .btn:first-child,
    .btn:focus,
    .btn:hover,
    button.btn.dropdown-toggle,
    button.btn.dropdown-toggle,
    .btn:hover,
    .btn:focus {
        background: #8ab4f8 !important;
        border: solid 2px #222;
        border-radius: 0px;
        text-shadow: none;
        box-shadow: none;
        color: #222;
    }
    .dropdown-menu > li > a {
        color: #fff;
    }
    .dropdown-menu {
        background-color: #222;
        border: solid 2px #414141 !important;
        border-radius: 0px !important;
        box-shadow: none !important;
    }
    button.btn {
        background: #222 !important;
        border: solid 2px #222;
        border-radius: 0px;
        box-shadow: none;
        text-shadow: none;
        color: #8ab4f8;
    }
    .caret {
        border-top: 4px solid #8ab4f8;
    }
    a,
    a:link,
    a:active,
    a:visited {
        color: #8ab4f8;
    }
    .topnav {
        background: #222;
        border: none;
    }
    .table-striped tbody > tr:nth-child(odd) > td,
    .table-striped tbody > tr:nth-child(odd) > th {
        background-color: #1e1e1e;
    }
    .table-bordered {
        border: none;
    }
    ul.topnav li a {
        padding: 10px 10px;
        border: none;
        text-transform: uppercase;
        color: #f3f3f3;
    }
    ul.topnav ul li a:hover,
    ul.topnav ul li.active {
        background-color: #8ab4f8;
        text-transform: uppercase;
    }
    .panel_normal {
        border-radius: 0px;
        color: #d5d5d5;
        border: none !important;
        outline: 0;
        background: #222;
    }
    .xgray,
    .gray {
        color: #818c9b;
    }
    .tcat {
        border: none !important;
        text-transform: uppercase;
        color: #ff881a;
        padding: 10px;
        font-size: 12px;
    }
    hr {
        background: #3d3d3d!important;
    }
    .table-bordered th,
    .table-bordered td,
    .tcat,
    #content_wrapper_inner {
        border-radius: 0;
        padding: 15px!important;
        color: #f1f1f1;
        border: 2px solid #393939!important;
        outline: 0;
        background: #161616!important;
    }
    .table-bordered {
        border-radius: 0px !important;
        border-color: #393939 !important;
    }
    b.xcontrast_txt {
        text-transform: uppercase;
        color: #ff881a;
    }
    li.divider {
        background: #ff881a !important;
        border-color: #ff881a !important;
    }
    .dropdown-menu > li > a:hover,
    .dropdown-menu > li > a:focus,
    .dropdown-submenu:hover > a,
    .dropdown-submenu:focus > a,
    ul.topnav li a:hover {
        background: #8ab4f8;
    }
    ul.topnav span {
        color: #8ab4f8;
    }
    div#p_footer {
        border: none;
    }
    .z-padtop {
        padding-top: 10px;
    }
    .panel_warning {
        background: #161616;
        color: #ff881a !important;
        border: solid 2px #333 !important;
    }
    .myborder_no_bottom,
    .myborder_only_bottom {
        border-color: #333 !important;
    }
    .grayback {
        background-color: #161616;
        border-color: #333 !important;
    }
    .mce-textbox:focus,
    .mce-textbox.mce-focus {
        border-color: #ff881a;
    }
    a.reviews {
        background: #ff881a;
        color: black;
        padding: 3px;
        padding-right: 10px;
        padding-left: 10px;
        border-radius: 10px;
        font-size: 12px;
        font-style: normal;
    }
    .mce-textbox {
        background: #222;
        border: 2px solid #333;
    }
    .mce-container,
    .mce-container *,
    .mce-widget,
    .mce-widget *,
    .mce-reset {
        color: #ff881a;
    }
    .mce-panel {
        background-color: #161616;
        border: solid 2px #333;
    }
    .mce-window-head {
        border-bottom: 2px solid #ff881a;
    }
    .mce-btn {
        border-color: #333 !important;
        position: relative;
        text-shadow: none;
        background: #ff881a !important;
    }
    .mce-ico {
        color: #222;
    }
    input#search_keywords,
    select#chap_select {
        background: transparent !important;
        border-radius: 0px;
        border: solid 2px #333;
        box-shadow: none;
        color: #8ab4f8;
        text-transform: uppercase;
    }

    textarea#review_review {
        color: #fff;
        background: #222 !important;
        border: solid 2px #333;
        padding: 20px;
        margin-bottom: 20px;
    }
    div#alert_subs {
        padding: 10px;
    }
    button#review_postbutton {
        text-transform: uppercase;
        color: #fff !important;
        background: #222 !important;
    }
}
