style.css:
@-moz-document url-prefix("https://scammer.info") {
	.d-header {
    display: flex;
    align-items: center;
    width: 100%;
    z-index: 1000;
    background-color: #0000006b;
    box-shadow: var(--shadow-header);
    backface-visibility: hidden;
}
.topic-list-item, tr {
    border-bottom: 2.5px solid #fff;
}
.category-list tbody .category h3 a[href], .category-list tbody .category h4 a[href] {
    color: #fff;
}
.category-list tbody:hover .category h3 a[href]:hover, .category-list tbody:hover .category h4 a[href]:hover, .custom-header-links:hover .headerLink a:hover {
    color: #6800ff;
}
.custom-header-links .headerLink a {
    padding: .35em .6em;
    color: var(--header_primary);
    font-size: var(--font-up-1);
}
.topic-list:hover .topic-list-item:hover {
    animation: shadows 1.5s infinite;
}
img.avatar:hover {
    transition: transform .5s ease;
    transform: scale(1.15);
    /*box-shadow: 0px 0px 5px 3px #6800ff;*/
    animation: avatarglow 1.5s infinite;
}
.avatar {
    border: 0px solid rgba(0, 0, 0, 0);
}
#site-logo {
    filter: hue-rotate(260deg);
}
#site-logo:hover {
    animation: logobounce 3s infinite;
}
@keyframes shadows {
    0% {
        text-shadow: #6800ff 0 0 10px;
    }
    50% {
        text-shadow: blueviolet 0 0 10px;
    }
    75% {
        text-shadow: rebeccapurple 0 0 10px;
    }
    100% {
        text-shadow: #6800ff 0 0 10px;
    }
}
@keyframes avatarglow {
    0% {
        box-shadow: 0px 0px 5px 3px #6800ff;
    }
    50% {
        box-shadow: 0px 0px 5px 3px blueviolet;
    }
    75% {
        box-shadow: 0px 0px 5px 3px rebeccapurple;
    }
    100% {
        box-shadow: 0px 0px 5px 3px #6800ff;
    }
}
@keyframes logobounce {
    25% {
        transform: scale(0.75);
        transition: transform 1s ease;
        filter: hue-rotate(240deg);
    }
    50% {
        transform: scale(1);
        transition: transform 1s ease;
        filter: hue-rotate(260deg)
    }
    75% {
        transform: scale(0.75);
        transition: transform 1s ease;
        filter: hue-rotate(240deg);
    }
    100% {
        transform: scale(1);
        transition: transform 1s ease;
        filter: hue-rotate(260deg)
    }
}
}
