style.css:
@-moz-document domain("filelist.io") {
html {
        background: url('https://i.postimg.cc/LX6D63Cq/File-List-Tema-Craciun.png') no-repeat center center fixed;
        -webkit-background-size: cover;
        -moz-background-size: cover;
        -o-background-size: cover;
        background-size: cover;
   }
   body {
         background: none;
   }
   a {
         color: #f50c0d;
   }
   .wrapper {
         background: none;
   }
   .mainheader {
         background: none;
   }
   .nblock-content a {
         color: #f50c0d;
   }
   #navigation {
         background-color: #db0f0f;
         border: 1px solid #cc0e0e;
         box-shadow: rgba(0, 0, 0, 0.4) 0px 2px 4px, rgba(0, 0, 0, 0.3) 0px 7px 13px -3px, rgba(0, 0, 0, 0.2) 0px -3px 0px inset;
         padding-top: 10px;
         padding-bottom: 10px;
         padding-left: 15px;
         padding-right: 10px;
         margin: 5px;
         top: 170px;
         width: 962px;
         height: 44px;
   }
   #navigation li a {
         color: #fff!important;
         text-shadow: 1px 1px 1px #111;
   }
   #navigation li a:hover {
        color: #fff!important;
        text-shadow: 1px 1px 1px #111;
   }
   #nav {
         height: auto;
         padding: 6px 20px;
         font-size: 13px;
         font-weight: bold;
         justify-content: center;
         align-items: center;
         display: flex;
   }
  .torrenttable {
         float: left;
         padding: 2px 0px;
         border: none;
         border-bottom: solid 1px rgba(253, 254, 255, 0.01);
  }

  .torrenttable img {
         border-radius: 9px;
  }
  .btn {
         align-items: center!important;
         appearance: none!important;
         background-image: radial-gradient(100% 100% at 100% 0, #f50c0d 0, #f50c0d 100%)!important;
         border: 0!important;
         border-radius: 6px!important;
         box-shadow: rgba(0, 0, 0, 0.4) 0px 2px 4px, rgba(0, 0, 0, 0.3) 0px 7px 13px -3px, rgba(0, 0, 0, 0.2) 0px -3px 0px inset;
         box-sizing: border-box!important;
         color: #ffffff!important;
         cursor: pointer!important;
         display: inline-flex!important;
         height: 38px!important;
         justify-content: center!important;
         line-height: 1!important;
         list-style: none!important;
         overflow: hidden!important;
         padding-left: 5px!important;
         padding-right: 5px!important;
         position: relative!important;
         text-align: center!important;
         text-decoration: none!important;
         transition: box-shadow .15s, transform .15s!important;
         user-select: none!important;
         -webkit-user-select: none!important;
         touch-action: manipulation!important;
         white-space: nowrap!important;
         will-change: box-shadow, transform!important;
         font-size: 12px!important;
   }
   .btn:focus {
         box-shadow: rgba(245, 12, 13, .4) 0 2px 4px, rgba(245, 12, 13, .3) 0 7px 13px -3px, rgba(245, 12, 13, .5) 0 -3px 0 inset!important;
   }
   .btn:hover {
         box-shadow: rgba(245, 12, 13, .4) 0 2px 4px, rgba(245, 12, 13, .3) 0 7px 13px -3px, rgba(245, 12, 13, .5) 0 -3px 0 inset!important;
         transform: translateY(-2px)!important;
   }
   .btn:active {
         box-shadow: #3c4fe0 0 3px 7px inset!important;
         transform: translateY(2px)!important;
   }
   .torrenttable a {
         color: #f50c0d!important;
         text-shadow: 1px 1px 1px #111;
   }
   .cblock-innercontent table form {
         padding: 10px -5px 10px 10px!important;
   }
   .noborder input, select {
         background: #212121;
         border: 1px solid #1c1c1c;
         padding: 10px 1px 10px 10px;
         color: #fff;
   }
   .noborder input:hover, select:hover {
         background: #212121;
         border: 1px solid #1c1c1c;
         padding: 10px 1px 10px 10px;
         color: #fff;
   }
   .statusbar {
         border: 1px solid #333;
         background: #222;
         box-shadow: rgba(0, 0, 0, 0.4) 0px 2px 4px, rgba(0, 0, 0, 0.3) 0px 7px 13px -3px, rgba(0, 0, 0, 0.2) 0px -3px 0px inset;
         text-align: center;
   }
   .statusbar a {
         color: #f50c0d;
   }
   #container a {
         color: #db0f0f;
   }
   .cblock, .cblockcontent {
         border-radius: 6px!important;
   }
   .cblock-top, .cblock-bottom {
         display: none;
   }
   .cblock-content {
         width: 952px;
         padding: 7px;
         background: rgba(0, 0, 0, 0.14);
         position: relative;
         border-radius: 10px;
         border-left: 4px solid #1c1c1c;
         border-right: 4px solid #1c1c1c;
         border-top: 4px solid #1c1c1c;
         border-bottom: 4px solid #1c1c1c;
   }
   .cblock-headerleft, .cblock-header, .cblock-headerright {
         position: relative;
         bottom: 0px;
   }
   .cblock-content, .cblock-innercontent {
         background: rgba(19, 19, 19, 1);
   }
   .nblock-content {
         width: 683px;
         border-radius: 10px;
         border: 0px solid #333;
         background: #222;
         box-shadow: rgba(0, 0, 0, 0.4) 0px 2px 4px, rgba(0, 0, 0, 0.3) 0px 7px 13px -3px, rgba(0, 0, 0, 0.2) 0px -3px 0px inset;
   }
   .sblock-content {
         width: 278px;
         height: 100%;
         border-radius: 10px;
         background-color: #131313;
         border: 0px solid #252525;
         box-shadow: rgba(0, 0, 0, 0.4) 0px 2px 4px, rgba(0, 0, 0, 0.3) 0px 7px 13px -3px, rgba(0, 0, 0, 0.2) 0px -3px 0px inset;
   }
   .colhead {
         background-color: #131313;
         border: 1px solid #252525;
   }
   .nblock-headerleft, .nblock-header, .nblock-headerright {
        background-color: #523434;  
   }
   .sblock-headerleft, .sblock-header, .sblock-headerright {
         background-color: #523434;
   }
   .cblock-headerleft, .cblock-header, .cblock-headerright {
         background-color: #DB0F0F;
   }
   .logo {
         position: relative;
         top: +10px;
         left: 80px;
         background: transparent url('https://i.postimg.cc/XvV5hpgy/File-List-Logo2.png') no-repeat;
         margin-top: 5px;
         max-height: 130px;
         min-width: 250px;
         padding-bottom: 20px;
         width: 400px;
         height: 180px;
   }
   ::-webkit-scrollbar {
         width: 1.4em;
   }
   ::-webkit-scrollbar-track {
         background: transparent;
   }
   ::-webkit-scrollbar-thumb {
         background-color: #db0f0f;
   }
   #poll_separator {
         margin-top: 5px;
         padding-top: 5px;
         border-top: 1px solid rgba(237, 248, 255, 0.01);
   }
   .mainheader {
         width: 99%;
         height: 245px;
         min-width: 1000px;
   }
   .status_avatar {
         margin:6px 41px 6px 6px;
         border: 2px solid;
         border-color: #5f35b;
         width:55px;
         height:65px;
   }
   body {
         letter-spacing: 0.2px;
         line-height: 14px;
   }
   input.btn {
         border: none !important;
         color: #fff !important;
         box-shadow: none !important;
   }
   select option {
         color: #fff !important;
   }
   .colhead {
         font-weight: bold;
         color: #E0E0E0;
         background: #151a1d;
         border: 1px solid #444;
         padding: 5px;
         margin-left: 0px;
   }
   td.colhead {
         margin-left: 8px; 
   }
   div#footer {
         background-color: #db0f0f;
   }
   div#copyright {
         background-color: #0C090A;
   }
   #copyrightInner {
         width: 970px;
         margin: 0 auto;
         font-size: 11px;
         color: #db0f0;
         padding: 13px 0;
         position: relative;
   }
   .nblock-innercontent {
         position: relative;
         bottom: 2px;
         top: -8px;
   }
   .nblock-bottom {
         width: 683px;
         height: 6px;
         display: block;
         background: #222; 
         margin-bottom: 20px;
   }
   .sblock-bottom {
         width: 272px;
         height: 100%;
         background-color: #131313;
         border: 3px solid #252525;
   }
   #maincolumn {
         width: 986px;
         min-height: 400px;
         margin: 0 auto;
   }
   .subheader {
         width: 995px;
         height: 150px;
         margin: 0 auto;
         position: relative;
   }
   .sblock-headerleft, .sblock-headerright {
         background: #222; 
   }
   .nblock-headerleft,  .nblock-headerright {
         background: #222; 
   }
   .cblock-headerleft,  .cblock-headerright {
         background: #222; 
   }
   .nblock-header {
         background-color: #523434; 
         border-radius: 10px;
         height: 30px;
         width: 673px;
    }
    .sblock-header {
         background-color: #523434; 
         border-radius: 10px;
         width: 268px;
         height: 30px;
    }
    .cblock-header {
         background-color: #523434; 
         border-radius: 10px;
         width: 961px;
         height: 30px;
    }
    .nblock-header h4 {
         padding-left: 12px;
         line-height: 30px;
         font-family: 'Trebuchet MS';
         font-weight: bold;
         font-size: 11pt;
        color: #E0E0E0;
        float: left;
    }
    .sblock-header h4 {
         padding-left: 12px;
         line-height: 30px;
         font-family: 'Trebuchet MS';
         font-weight: bold;
         font-size: 11pt;
         color: #E0E0E0;
         float: left;
    }
    .cblock-header h4 {
         padding-left: 12px;
         line-height: 30px;
         font-family: 'Trebuchet MS';
         font-weight: bold;
         font-size: 11pt;
         color: #E0E0E0;
         float: left;
    }
    td.colhead {
         font-size: 7,5pt;
    }
    #sectionmain {
         width: 95%;
         margin: 15px 0px 0px 17px;
    }
    #forummain {
         width: 95%;
         margin: 0 auto;
         margin-top: 0px;
         margin-right: auto;
         margin-bottom: 0px;
         margin-left: 17px;
    }
    .main {
         width: 710px;
         height: 180px;
         margin: 0 auto;
         color: #FFFFFF;
         background: #1f1f1f;
         border-radius: 5px;
    }
    hr.separator {
        border: 0;
        height: 3px;
        width: 600px;
        margin: 11px auto 0px auto;
        background-image: -webkit-linear-gradient(left, rgba(0,0,0,0), rgba(0,0,0,0.75), rgba(0,0,0,0));
    }
    #poll_separator {
         margin-top: 5px;
         padding-top: 5px;
         border-top: 1px solid #444;
    }
    .small {
         font-size: 7.5pt;
    }
   .staffbox {
         width: 100px;
         border: 1px solid #cacaca;
         padding: 5px;
         margin: 0px 4px 10px 6px;
         background: rgba(19, 19, 19, 1);
         text-align: center;
    }
}
