style.css:
@-moz-document url-prefix("https://boardgamegeek.com/subscriptions") {
gg-feed-item .item-icon {
        display: none !important;
    }
    
    gg-feed-item .feed-item {
        margin: 0 !important;
        min-height: 0 !important;
        padding-top: 5px !important;
        padding-bottom: 5px !important;
    }
    
    gg-feed-item div.item-icon {
        width: 0 !important;
        height: 0 !important;
    }
    
    gg-feed-item gg-breadcrumbs gg-item-link-ui {
        font-size: 12px !important;
    }
    
    gg-feed-item button.item-sub-detail > span {
        display: none !important;
    }
}
