style.css:
@-moz-document domain("www.rapideo.net") {
a {
        color: #a6a6a6;
        text-decoration: none;
    }
    a:hover {
        color: #e6e6e4;
        text-decoration: none;
    }  
    .box-reqister {
        background: linear-gradient(to top, #11293c 10%, #054164 100%);
        border-radius: 10px;
        padding: 30px 35px;
        -webkit-box-shadow: 5px 5px 5px rgba(0,0,0,0.15);
        box-shadow: 0-1px 2px #11293c;
        box-shadow: 0 5px 12px rgba(0, 0, 0, .81);
    }
    .visual {
        background: linear-gradient(to top, #11293c 0%, #054164 100%);
    }
    body {
        background: #11293c;
        color: #a2a2a2;
    }
    element.style {
        background: bottom no-repeat #0c2336;
    }
    .wrapper {
        background: #11293c;
    }
    div.easy-autocomplete {
        box-shadow: 0-1px 2px #11293c;
        box-shadow: 0 5px 12px rgba(0, 0, 0, .81);
    }
    div.column_right {
        background: #11293c;
    }
    .column_left_text {
        background: #11293c;
    }
    .main {
         background: #11293c;
    }
    .dscTxt {
        color: #a2a2a2;
        margin-left: 10px;
        margin-right: 10px;
    }
    .dscLinks {
        color: #a2a2a2;
        margin-left: 10px;
        margin-right: 10px;
    }
    .logIn .column_right {
        background: #11293c;
        border-left: 2px solid #11293c;
    }
    #footer {
        margin-top: 20px;
        background: linear-gradient(to top, #11293c 0%, #054164 100%);
        padding: 55px 0 10px 0;
        font-size: 12px;
        line-height: 2;
    }
    footer {
        background: #11293c;
        border-top: 0px solid #e6e6e6;
    }
    .info_notice.promo {
        /*
        background: #11293c;
        */
        background: linear-gradient(to top, #054164 0%, #11293c 70%);
        box-shadow: 0-1px 2px #11293c;
        box-shadow: 0 5px 12px rgba(0, 0, 0, .81);
        border-radius: 10px;
        margin-left: 22px;
    }
    html, button, input, select, textarea {
        color: #aaa;
    }
    #insertPanel #filesList {
        width: 71%!important;
    }
    #insertPanel #dlc-download {
    width: 24%!important;
    }
    .filesArea {
        margin-left: 5px;
        margin-right: 5px;
        width: 100%;
        height: 152px;
        -webkit-border-radius: 10px;
        -moz-border-radius: 10px;
        border-radius: 10px;
        padding: 10px 15px;
        border: 1px solid #cccccc;
        background: #0c2336;
        box-shadow: 0 0 20px #061521 inset;
        /*box-shadow: 0-1px 2px #11293c;
        box-shadow: 0 5px 12px rgba(0, 0, 0, .81);*/
    }
    td {
        background: #11293c;
        color: #babbbb;
        overflow: hidden;
        border: 1px solid #11293c;
        -webkit-border-radius: 5px;
        -moz-border-radius: 5px;
        border-radius: 5px;
        margin-top: 30px;
    }
    .dlc-download.filesArea {
        color: #a2a2a2;
    }
    .info_notice.green {
        box-shadow: 0-1px 2px #11293c;
        box-shadow: 0 5px 12px rgba(0, 0, 0, .81);
    }
    .right_menu.show-for-large-up {
        background: no-repeat linear-gradient(to top, #11293c 100%, #054164 10%);
        /*background: center bottom no-repeat #11293c;*/
        -webkit-border-radius: 10px;
        -moz-border-radius: 10px;
        border-radius: 10px;
        color: #999999;
        line-height: 1;
        margin-top: 15px;
        border: 1px solid #efefef;
        padding: 30px 0 2px 0;
        box-shadow: 0-1px 2px #11293c;
        box-shadow: 0 5px 12px rgba(0, 0, 0, .81);
    }
    div.form-progress::after {
        background: #11293c;
    }
    .pakiet-inside {
        box-shadow: 0-1px 2px #11293c;
        box-shadow: 0 5px 12px rgba(0, 0, 0, .81);
    }
    .panel-dashed {
        padding: 40px 25px;
        border: 1px solid #5eb20a;
        background: linear-gradient(to top, #054164 0%, #11293c 30%);
        border-radius: 10px;
        position: relative;
        margin: 40px 0;
        box-shadow: 0-1px 2px #11293c;
        box-shadow: 0 5px 12px rgba(0, 0, 0, .81);
    }
    .button-lg {
        box-shadow: 0-1px 2px #11293c;
        box-shadow: 0 5px 12px rgba(0, 0, 0, .81);
    }
    .panel-dashed:before, .panel-dashed:after {
        content: none;
        position: absolute;
    }
    .section.payment-finalize {
         background: #11293c;
    }
    span.figure {
        background: #11344f;
        box-shadow: 0-1px 2px #11293c;
        box-shadow: 0 5px 12px rgba(0, 0, 0, .81);
    }
    .table_box.table_box_blue .table_headline {
        color: #868d93;
        background-color: #0c253b;
    }
    .table_box.table_box_green .table_headline {
        color: #868d93;
        background-color: #0c253b;
        border-color: #a5bacb;
    }
    .table_box.table_box_blue {
        box-shadow: 0-1px 2px #11293c;
        box-shadow: 0 5px 12px rgba(0, 0, 0, .81);
    }
    .table_box.table_box_green {
        border-color: #a5bacb;
        box-shadow: 0-1px 2px #11293c;
        box-shadow: 0 5px 12px rgba(0, 0, 0, .81);
    }
    .table_footer {
        background: #0c253b;
    }
    .right_menu ul {
        margin: 0 1px;
        padding: 0 0 20px 0;
        background: linear-gradient(to top, #054164 0%, #11293c 30%);
        /*background: center bottom no-repeat #11293c;*/
    }
    .boxContentBg {
        padding: 20px 0 30px 0;
        border-radius: 10px;
        background: linear-gradient(to top, #054164 0%, #11293c 30%);
        /*
        background: url(https://static.rapideo.net/css/../images/boxContentBg.png) bottom repeat-x;
        */
        box-shadow: 0-1px 2px #11293c;
        box-shadow: 0 5px 12px rgba(0, 0, 0, .81);
    }
    .buttonShadowCircle {
        box-shadow: 0-1px 2px #11293c;
        box-shadow: 0 5px 12px rgba(0, 0, 0, .81);
    }
    div.portalDsc {
        background: linear-gradient(to top, #054164 0%, #11293c 30%);
        box-shadow: 0-1px 2px #11293c;
        box-shadow: 0 5px 12px rgba(0, 0, 0, .81);
        border-radius: 10px;
        border: 1px solid #efefef;
        padding: 10px;
    }
    .steps {
        width: 582px;
        margin-left: 10px;
        margin-right: 10px;
    }
}
