style.css:
@media (min-width: 768px) { /* md breakpoint */
    .md\:gap-6 {
        gap: 1.5rem !important;
    }

    .gizmo\:md\:px-5 {
        padding-left: 1.25rem !important;
        padding-right: 1.25rem !important;
    }

    .gizmo\:md\:max-w-3xl, .md\:max-w-2xl {
        max-width: 136rem !important; /* Adjust as needed */
    }
}

@media (min-width: 1024px) { /* lg breakpoint */
    .lg\:max-w-\[138rem\], .gizmo\:lg\:max-w-\[140rem\] {
        max-width: 138rem !important; /* Adjust as needed */
    }
  
    .gizmo\:lg\:px-1 {
        padding-left: 0.25rem;
        padding-right: 0.25rem;
    }
}

@media (min-width: 1280px) { /* xl breakpoint */
    .gizmo\:xl\:max-w-\[148rem\], .xl\:max-w-3xl {
        max-width: 148rem !important; /* Adjust as needed */
    }

    .gizmo\:xl\:px-5 {
        padding-left: 1.25rem;
        padding-right: 1.25rem;
    }
}


/* Media query for medium devices (md) */
@media (min-width: 768px) {
    .md\:px-5 {
        padding-left: 1.25rem;
        padding-right: 1.25rem; /* Sets horizontal padding to 20px on medium devices */
    }

    .md\:max-w-3xl {
        max-width: 148rem  !important; /* Sets max-width to 768px on medium devices */
    }
}

/* Media query for large devices (lg) */
@media (min-width: 1024px) {
    .lg\:px-1 {
        padding-left: 0.25rem;
        padding-right: 0.25rem; /* Sets horizontal padding to 4px on large devices */
    }

    .lg\:max-w-\[40rem\] {
        max-width: 140rem  !important;  /* Sets max-width to 640px on large devices */
    }
}

/* Media query for extra large devices (xl) */
@media (min-width: 1280px) {
    .xl\:px-5 {
        padding-left: 1.25rem;
        padding-right: 1.25rem; /* Sets horizontal padding to 20px on extra large devices */
    }

    .xl\:max-w-\[48rem\] {
        max-width: 148rem !important;  /* Sets max-width to 768px on extra large devices */
    }
}
