style.css:
@-moz-document domain("chess.com") {
.clock-component {
  font-size: 5rem !important;
  padding-left: 200px !important;
  background-color: transparent !important;
}

.clock-icon-icon {
  opacity: 0 !important;
}

.clock-time-monospace {
  color: lime !important;
  text-shadow: 
        -1px -1px 0 #000,
         1px -1px 0 #000,
        -1px  1px 0 #000,
         1px  1px 0 #000;
}

.clock-component:not(.clock-player-turn) {
    opacity: .3 !important;
    filter: grayscale(1) !important;
}

.move-time-time  {
  font-size: 5rem !important;
  background-color: transparent !important;
  color: lime !important;
  text-shadow: 
        -1px -1px 0 #000,
         1px -1px 0 #000,
        -1px  1px 0 #000,
         1px  1px 0 #000;
}

.move-time-inactive {
    opacity: .3 !important;
    filter: grayscale(1) !important;
}

.move-time-content {
    overflow: visible !important;
}
}
