style.css:
@-moz-document domain("chatgpt.com") {
.m-auto {
    margin: 1%;
    margin-top: 1%;
	margin-bottom: 1%;
}
  
  /* Menu Closed */
.mx-auto .md\:max-w-3xl {
	max-width: 75% !important;
}


.lg\:max-w-\[40rem\] {
  max-width: 75% !important;
}

.py-2.px-3.text-base.md\:px-4.md\:py-5.lg\:px-5.xl\:px-5 {
   max-width: 75% !important;
}

@media (min-width: 768px) {
    .md\:max-w-3xl {
        max-width: 75% !important;
    }
}

@media (min-width: 1024px) {
    .lg\:max-w-\[40rem\] {
        max-width: 75% !important;
    }
}

.overflow-auto {
	overflow: clip;
}

/* Menu Opened */
.xl\:max-w-3xl {
	max-width: 75%;
  }
}
