style.css:
/* i really want this to be global */
body main .lh-topbar,
.lh-scores-header > a:nth-of-type(4),
.lh-report .lh-categories > div:nth-of-type(4) { display: none; }
body main .lh-container::before {
    content: '';
    display: block;
    height: 100vh;
    width: 100%; 
    background:url('https://rotapix.com/wp-content/uploads/2022/11/rotapix-logo.jpg');
    background-repeat: no-repeat;
    background-position: center;
    background-size:300px;
}
