style.css:
@-moz-document domain("app.slack.com") {
.p-bookmarks_bar--with-hide-on-scroll,
.p-view_header,
.p-flexpane--ia_details_popover .p-flexpane_header,
.p-ia4_client .p-client_workspace__layout,
.p-ia4_client .p-view_contents--primary,
.p-ia4_client .p-view_contents--secondary {
    box-shadow: none !important;
}

.c-button--outline {
    filter: invert(0.1) !important;
}

.sk-client-theme--dark .p-ia4_client .p-view_contents--sidebar,
.p-upgrades_button,
.sk-client-theme--dark .p-ia4_client .p-client_workspace__layout,
.c-wysiwyg_container--theme_dark_bordered,
.c-button--outline,
.p-r_member_profile_section,
.p-ia4_client .p-view_contents--secondary {
    border: none !important;
}

.p-ia4_client .p-client_workspace__layout {
    border-radius: 0px !important;
}

.p-ia4_client .p-client_workspace {
    padding: 0px !important;
    margin: 0px !important;
}


.p-ia4_client .p-ia4_top_nav {
    background: var(--toolbar--bg) !important;
    height: var(--toolbar-sizing) !important;
    top: 0 !important;
    bottom: unset !important;
}

.p-unread_dot {
    display: none !important;
}

.p-tab_rail__button .c-tabs__tab_content,
.p-ia4_client--theming .p-ia4_history_menu_button__button,
.p-ia4_client--theming .p-ia4_history_buttons__button,
.p-control_strip__circle_button,
.p-ia4_client--theming .p-top_nav__button,
.p-top_nav__search,
.p-top_nav__search__clear,
.p-top_nav__search__filters {
    color: var(--toolbar--text) !important;
}

.p-ia4_channel_list .p-channel_sidebar__section_heading--selected,
.p-ia4_channel_list .p-channel_sidebar__channel--selected,
.p-ia4_channel_list .p-channel_sidebar__channel--selected:hover,
.p-ia4_channel_list .p-channel_sidebar__link--selected,
.p-ia4_channel_list .p-channel_sidebar__link--selected:hover,
.p-add_team_label__icon {
    background: var(--sidebarhighlights--bg) !important;
    color: var(--sidebarhighlights--text) !important;
}

.p-ia4_client--theming .p-ia4_channel_list .p-channel_sidebar__channel--selected,
.p-ia4_client--theming .p-ia4_channel_list .p-channel_sidebar__link--selected,
.p-ia4_client--theming .p-ia4_channel_list .p-channel_sidebar__channel--selected+.p-channel_sidebar__close,
.p-ia4_client--theming .p-ia4_channel_list .p-channel_sidebar__link--selected+.p-channel_sidebar__close {
    color: var(--sidebarhighlights--text) !important;
}
.p-ia4_client--theming .p-channel_sidebar__channel--selected .p-channel_sidebar__member_label {

    color: var(--sidebarhighlights--text) !important;
    opacity: 0.5;
}

.c-menu_item__button--highlighted,
.c-menu_item--standalone:hover,
.no_touch a.c-menu_item__button--highlighted,
.c-menu_item__button--highlighted:link,
.c-menu_item--standalone:hover:link,
.no_touch a.c-menu_item__button--highlighted:link,
.c-menu_item__button--highlighted:visited,
.c-menu_item--standalone:hover:visited,
.no_touch a.c-menu_item__button--highlighted:visited,
.c-search_autocomplete__suggestion_item--selected{
    background: #8080801a !important;
    color: var(--sk_primary_foreground) !important;
}



.c-base_list_entity--highlight-dark,
.c-search_autocomplete__suggestion_item--selected .c-icon,
.c-search_autocomplete__suggestion_item--selected .c-file_entity__icon:before,
.c-search_autocomplete__suggestion_item--selected .c-file_entity__detail,
.c-search_autocomplete__suggestion_item--selected .c-search_autocomplete__channel .c-deprecated-icon,
.c-search_autocomplete__suggestion_item--selected .c-search_autocomplete__channel .p-deprecated_channel_name__text,
.c-search_autocomplete__suggestion_item--selected .c-search_autocomplete__channel .c-icon--shared-channels,
.c-search_autocomplete__suggestion_item--selected .c-search_autocomplete__channel .p-deprecated_channel_name__team_name,
.c-search_autocomplete__suggestion_item--selected .c-search_autocomplete__channel-membership,
.c-search_autocomplete__suggestion_item--selected .c-page_view_entity__icon,
.c-search_autocomplete__suggestion_item--selected .c-search_query_entity__icon {
    background: none !important;
    color: var(--sk_primary_foreground) !important;
}

.p-ia4_channel_list .p-channel_sidebar__channel:not(.p-channel_sidebar__channel--selected):hover,
.p-ia4_channel_list .p-channel_sidebar__link:not(.p-channel_sidebar__link--selected):hover,
.p-ia4_channel_list .p-channel_sidebar--iap1 .p-channel_sidebar__section_heading_plus:hover,
.p-ia4_channel_list .p-channel_sidebar--iap1 .p-channel_sidebar__section_heading_ellipsis:hover,
.p-ia4_channel_list .p-channel_sidebar--iap1 .p-channel_sidebar__section_heading_expand_container:hover,
.p-ia4_channel_list .p-channel_sidebar--iap1 .p-channel_sidebar__section_heading_button:hover,
.p-ia4_home_header_menu__button:hover {
    background: var(--subhighlights--bg) !important;
    background-color: var(--subhighlights--bg) !important;
}


.p-ia4_client .p-ia4_top_nav__middle_container .p-top_nav__search__container {
    border-bottom: 1px dashed var(--toolbar--text);
    border-radius: 0px !important;
    background: #ffffff17 !important;
}

.p-r_member_profile_section .c-link,
.p-r_member_profile_section .c-link--button,
.p-rimeto_profile_sections__element--edit,
.p-message_pane_message__message_label .c-message_kit__labels__label,
.p-custom_status_modal__edit_presets,
.p-prefs_dialog__modal.p-prefs_dialog__modal--ia_layout a {
    color: var(--highlights--text) !important;
}

.c-tabs__tab.c-tabs__tab--active,
.c-tabs__tab:active {
    box-shadow: inset 0 -2px 0 0 var(--highlights--2, #007a5a);
}

.c-message_kit__background--labels--later {
    background: #8484841f !important;
}
.c-member_slug--link,
.p-video_composer_button--highlight.c-button-unstyled,
.p-video_composer_button--highlight.c-button-unstyled:hover,
.p-audio_composer_button--highlight.c-button-unstyled {
    background: #77777721 !important;
    color: var(--sk_primary_foreground) !important;
}


.p-activity_ia4_page__item--selected,
.p-activity_ia4_page__item--selected:hover {
    background-color: #87878724 !important;
}

.p-more_menu_icon {
    background: #82828221 !important;
    color: var(--dt_color-plt-gray-80) !important;
}

.p-top_nav__search:hover {
    background: #ffffff29 !important;
    border-radius: 0px !important;
}

.p-workspace_banner__download_ia4 {
    background: #f000 !important;
}

.c-coachmark-anchor {
    position: static !Important;
}

.p-ia4_client .p-client_workspace_wrapper {
    grid-template: "p-tab-rail p-client-workspace" / 0px auto !important;
}

.p-ia4_client .p-ia_banner {
    margin: 0px !Important;
    border-radius: 0px !important;
}

/*left tb*/
.p-tab_rail {
    padding: 0px;
    position: fixed !important;
    top: 0px !important;
    bottom: unset !important;
    display: flex;
    flex-direction: row;
    flex-wrap: nowrap;
    justify-content: flex-start;
    align-content: stretch;
    height: var(--toolbar-sizing) !important;
    width: auto !important;
    align-items: center !important;
    column-gap: var(--toolbar-column-gap) !important;

    margin-left: var(--toolbar-column-gap) !important;
}

.p-tab_rail__tab_menu {
    position: relative !important;
    flex-direction: row;
    flex-wrap: nowrap;
    justify-content: flex-start;
    align-content: stretch;
    column-gap: var(--toolbar-column-gap) !important;
    height: var(--toolbar-sizing) !important;
    align-items: center !important;
}

/*right tb*/
.p-control_strip {
    position: fixed !important;
    right: 50px !important;
    top: 0 !important;
    bottom: unset !important;
    left: unset !important;
    align-items: center !important;
    height: var(--toolbar-sizing) !important;
    padding: 0px !important;
    column-gap: var(--toolbar-column-gap) !important;
}


.p-control_strip,
.p-ia__nav__user,
.c-tabs__tab_content,
.p-tab_rail__button.p-tab_rail__button--active .c-tabs__tab_content,
.p-tab_rail__tab_menu.c-tabs__tab_menu--vertical .c-tabs__tab,
.p-peek_trigger,
.p-tab_rail__button .c-tabs__tab_content {
    display: flex;
    flex-direction: row;
    flex-wrap: nowrap;
    justify-content: flex-start;
    align-content: stretch;
    align-items: center !important;
}

.p-control_strip .p-ia__nav__user__status_icon {
    display: none !important;
}


.p-tab_rail__button .c-tabs__tab_content {
    padding: 0px !important;
    width: auto;
}

.p-ia4_client .p-view_contents--sidebar.p-view_contents--channel-list-pry,
.p-ia4_client .p-view_contents--sidebar.p-view_contents--channel-list-ter {
    background-color: rgba(var(--sk_primary_background, 255, 255, 255), 1) !important;
}

.p-ia4_client .p-ia4_top_nav__container_wrapper {
    flex: none !important;
    width: 540px !important;
}

.p-control_strip__circle_button,
.p-tab_rail__button:hover:not(.p-tab_rail__button--setup) .p-tab_rail__button__icon,
.p-peek_trigger--is-peek-visible .p-tab_rail__button .p-tab_rail__button__icon,
.p-ia4_channel_list .p-channel_sidebar__channel--selected+.p-channel_sidebar__close,
.p-ia4_channel_list .p-channel_sidebar__link--selected+.p-channel_sidebar__close {
    background: none !important;
}

.p-ia4_top_nav__left_container {
    flex-basis: 0!important;
}

.p-tab_rail__button:active:not(.p-tab_rail__button--setup) .p-tab_rail__button__icon,
.p-tab_rail__button.p-tab_rail__button--active .p-tab_rail__button__icon,
.p-tab_rail__button.p-tab_rail__button--active:hover .p-tab_rail__button__icon {
    background: none !important;
}

.p-ia4_client .p-view_contents--primary,
.p-ia4_client .p-view_contents--secondary {
    border-left: 1px solid rgba(var(--sk_foreground_low_solid, 221, 221, 221), 1) !important;
}


.p-ia4_client .p-ia4_top_nav__left_container .p-ia4_top_nav__left_container--end {
    margin-right: var(--toolbar-column-gap) !important;
}



.c-popover--fade .ReactModal__Content {
    top: 60px !important;
}

.c-tooltip__tip--right .c-tooltip__tip__arrow {
    top: 0px !important;
    left: 50% !important;
}


.c-popover__content {
    z-index: 999999999 !important;
}




.p-ia4_client.p-ia4_client--with-search-in-top-nav .p-view_contents--primary,
.p-ia4_client.p-ia4_client--with-search-in-top-nav .p-view_contents--secondary,
.p-ia4_client.p-ia4_client--with-search-in-top-nav .p-view_contents--sidebar,
.p-ia4_client.p-ia4_client--with-search-in-top-nav .p-view_contents--split {
    max-height: calc(100vh) !important;
}


:root {
    --toolbar-sizing: 55px;
    --toolbar-column-gap: 20px;
}

/* Optional */
:root {
    --toolbar--bg: "/*[toolbarbg]*/" !important;
    --toolbar--text: #eee;

    --sidebarhighlights--bg: #e8e8e8;
    --sidebarhighlights--text: #000;


    --highlights--bg: #d0d0d0;
    --highlights--text: #006a38;

    --highlights--2: #048054;

    --subhighlights--bg: #8686861a;

    --dt_color-plt-gray-80: 38, 38, 38;
    --dt_color-plt-gray-90: 38, 36, 38;

    --dt_color-base-inv-hgl-1: var(--highlights--2);

    --dt_color-theme-base-inv-hgl-2: var(--accents--) !important;
    
    --accents--: #C96E16;
}



body,
[lang] .c-texty_input_unstyled__container--size_medium .ql-editor,
[lang] .c-texty_input_unstyled__container--size_medium .ql-placeholder,
.c-menu {
    font-family: Monospace !important;
}





.p-account_switcher {
    opacity: 1;
    width: 240px !important;
    height: 36px;
    position: relative;
    background-image: url("https://i.imgur.com/PrWRxcP.png") !important;
    background-size: cover !important;
    background-color: #00800000 !important;
}

.c-team_icon:not(.c-team_icon--resetIA) {
    display: none !important;
}
}
