style.css:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

/* Set the background color to dark gray */
body {
  background-color: #1e1e1e;
  color: #ffffff;
}

/* Style the buttons */
button {
  background-color: #282828;
  border: none;
  padding: 10px 20px;
  cursor: pointer;
  font-size: 16px;
  color: #ffffff;
  transition: background-color 0.3s;
}

button:hover {
  background-color: #343434;
}

/* Style the links */
a {
  color: #888888;
  text-decoration: none;
}

a:hover {
  color: #bbbbbb;
}

/* Style the input fields */
input, textarea {
  background-color: #282828;
  border: none;
  padding: 10px;
  font-size: 16px;
  color: #ffffff;
  margin-bottom: 20px;
}

/* Style the checkboxes and radio buttons */
input[type="checkbox"], input[type="radio"] {
  background-color: #282828;
  border: none;
  padding: 5px;
}

/* Style the dropdown menus */
select {
  background-color: #282828;
  border: none;
  padding: 10px;
  font-size: 16px;
  color: #ffffff;
}

/* Style the table elements */
table {
  border-collapse: collapse;
  width: 100%;
}

table, th, td {
  border: 1px solid #343434;
}

th {
  background-color: #282828;
  color: #ffffff;
}
