style.css:
@-moz-document url-prefix("https://news.ycombinator.com") {
body {
	  max-width: 700px;
	  margin: 0 auto;
	  background-color: #333;
	}

	td {
	  padding: 2px;
	  line-height: 1.4em;
	}

	td.title {
	  font-size: 11pt;
	}

	.subtext {
	  font-size: 10pt;
	}

	.comment {
	  font-size: 11pt;
  }
  
  pre {
  	font-size: 10pt;
  }
  
  



  .toptext {
  	font-size: 11pt;
  	color: #000;
	}

.comhead {
  	  font-size: 10pt;
  }
}
