style.css:
@-moz-document domain("www.youtube.com") {
#progress.ytd-thumbnail-overlay-resume-playback-renderer {
    background-color: #FF0000;
	}
	#progress.ytd-thumbnail-overlay-resume-playback-renderer[style*="100%"] {
		background: #fff0;
	}
	ytd-thumbnail-overlay-resume-playback-renderer {
		background-color: #1a1a1a00
	}
	#progress.ytd-thumbnail-overlay-resume-playback-renderer[style*="100%"]::before {
		content: 'Watched';
		position: absolute;
		height: 600%!important;
		width: 100%;
		top: -25px;
		color: #fff!important;
		display: flex;
		align-items: center;
		justify-content: left;
		font-size: 130%;
		padding-left: 9px;
		letter-spacing: 0.05em;
		text-shadow: 0 0 9px #0b0b0b66;
		background: linear-gradient(-90deg, #0000 10%, #000 100%);
		transition: all 1s ease-in-out 1s;
	}
	#thumbnail:hover #overlays #progress.ytd-thumbnail-overlay-resume-playback-renderer[style*="100%"]::before {
		opacity: 0;
		content: 'Watch Again?';
	}
}
