style.css:
@-moz-document url-prefix("https://www.pinterest.com/") {
/* Set background color for the entire page */
  body {
    background-color: #ffe0f3;
  }

  /* Import Coolvetica font */
  @font-face {
    font-family: 'Coolvetica';
    src: url('https://drive.google.com/uc?export=download&id=1VEi-P_E5ug6uoUg8lIzaPl1wk4dohj2Y'); 
  }

  /* Set text color to #FC8FD2 for specific elements */
    .tBJ,
    .dyH,
    .gUZ, 
    .Hn_, 
    .x8f, 
  /* ... other selectors ... */
  .swG {
    color: #FC8FD2 !important;
  }

  /* Set text color to #FFF for most elements */
  body:not(.tBJ):not(.dyH):not(.iFc):not(.sAJ):not(.O2T):not(.zDA):not(.IZT):not(.H2s):not(.custom-cursor-default-hover):not(.gUZ):not(.R19):not(.U9O):not(.kVc):not(.Hn_):not(.lH1):not(.GjR):not(.UK7):not(.swG) {
    color: #FFF !important;
  }

  /* Set background color for specific elements */
    .custom-cursor-default-hover
    .RCK, .Hsu, .USg, .adn, .CCY, .NTm, .KhY, .S9z, .F10, .xD4, .fZz, .hUC, .Il7, .Jrn, .hNT, .BG7, .yui, .hDj, ._O1, .KS5, .mQ8, .Tbt, .L4E, .custom-cursor-default-hover{
    background-color: #ffe7f6 !important;
  }

  /* Set background color for the rest of the elements to #FFFFFF */
  body:not(.mainContainer):not(.CCY):not(.S9z):not(.Lfz):not(.Tbt):not(.L4E):not(.e8F):not(.BG7):not(.KS5):not(.hs0):not(.un8):not(.C9i):not(.TB_):not(.custom-cursor-default-hover):not(.fZz):not(.urM):not(.zI7):not(.iyn):not(.Hsu):not(.custom-cursor-default-hover) {
    background-color: #FFFFFF !important;
  }

  /* Background image properties */
  body::before {
    content: "";
    position: fixed;
    top: 30px; /* Adjust as needed */
    right: 30px; /* Adjust as needed */
    width: 200px; /* Adjust the width of the GIF */
    height: 200px; /* Adjust the height of the GIF */
    background-image: url('https://i.imgur.com/adHpm6C.gif');
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    z-index: 9999; /* Ensure it's above other elements */
  }

  /* Targeting #myDiv */
  #myDiv {
    /* Styles for this element with the ID 'myDiv' */
    /* Add your specific styles here */
  }
}
