style.css:
@-moz-document url("https://thepatternsite.com/"), url-prefix("https://thepatternsite.com/") {
/* Your CSS rules go here */
    body, body * {
        background-image: none !important;
        background-color: #f4f4f4 !important; /* Light grey background */
    }

    body, body * {
    background-image: none !important;
    background-color: #f4f4f4 !important; /* Light grey background */
      }

      body {
          font-family: 'Calibri', 'Arial Rounded MT Bold', sans-serif !important;
          font-size: 20px !important;
          color: #333333 !important;
      }

      h1, h2, h3, h4, h5, h6 {
          font-family: 'Calibri', 'Arial Rounded MT Bold', sans-serif !important;
          color: #0056b3 !important;
      }

      a:link, a:visited {
          color: #0056b3 !important;
      }

      a:hover, a:active {
          color: #ff6600 !important;
      }

      /* Improve spacing and padding */
      p, ul, ol {
          line-height: 1.6 !important;
          margin-bottom: 1em !important;
      }

      /* Adjust the header and footer */
      header, footer {
          background-color: #e6e6e6 !important;
      }

      /* Style adjustments for navigation */
      nav {
          background-color: #333 !important;
          color: white !important;
      }

      nav a:link, nav a:visited {
          color: white !important;
      }

      nav a:hover, nav a:active {
          color: #ff6600 !important;
      }

      /* Improve form inputs and buttons */
      input[type='text'], input[type='email'], input[type='password'], textarea {
          font-family: 'Calibri', 'Arial Rounded MT Bold', sans-serif !important;
          padding: 0.5em !important;
          margin: 0.5em 0 !important;
      }

      input[type='submit'], button {
          background-color: #0056b3 !important;
          color: white !important;
          padding: 0.5em 1em !important;
          border: none !important;
          border-radius: 4px !important;
          cursor: pointer !important;
      }

      input[type='submit']:hover, button:hover {
          background-color: #ff6600 !important;
      }
}
