:root {
    --sharechess-board-url: url(data:image/png;base64,);
    --sharechess-border-url: none;
    --sharechess-border-color: #00735a;
  }
}

@-moz-document domain("lichess.org") {
.is2d cg-board {
    background-image: var(--sharechess-board-url) !important;
  }
  .is2d coords {
    --cg-ccw: #d4fff5 !important;
    --cg-ccb: #00735a !important;
  }
  square.last-move {
    background-color: #ffff0055 !important;
  }
}

@-moz-document domain("chess.com") {
.board {
    background-image: var(--sharechess-board-url) !important;
  }
  .coordinate-light {
    fill: #00735a !important;
  }
  .coordinate-dark {
    fill: #d4fff5 !important;
  }
  .coords-light {
    color: #00735a !important;
  }
  .coords-dark {
    color: #d4fff5 !important;
  }
  .highlight,
  .square.move-square {
    opacity: 1 !important;
    background-color: #ffff0055 !important;
  }
}

@-moz-document domain("openingtree.com") {
.cg-wrap {
    background-image: var(--sharechess-board-url) !important;
    background-size: cover !important;
    filter: none !important;
  }
  square.last-move {
    background-color: #ffff0055 !important;
  }
}

@-moz-document domain("chessgames.com") {
[class^="board-"] {
    background-image: var(--sharechess-board-url) !important;
    background-size: cover !important;
  }

  [class^="square-"] {
    background: none !important;
  }

  [class^="square-"][class*="white-"] {
    color: #00735a !important;
  }

  [class^="square-"][class*="black-"] {
    color: #d4fff5 !important;
  }

  [class*="highlight1"],
  [class*="highlight2"] {
    background: #ffff0055 !important;
    box-shadow: none;
  }
}

@-moz-document domain("chess24.com") {
.chess-board .svg {
    background-image: var(--sharechess-board-url) !important;
    background-size: cover !important;
    opacity: 1 !important;
    border-radius: 2.8%;
  }

  .chess-board .svg > * {
    opacity: 0.6 !important;
    filter: brightness(0.5) sepia(1) hue-rotate(60deg) saturate(100);
  }

  [data-cy="chessboard"]
    > div:first-child
    > div:first-child
    > div:first-child
    > div
    > svg {
    background-image: var(--sharechess-board-url) !important;
    background-size: cover !important;
  }

  [data-cy="chessboard"]
    > div:first-child
    > div:first-child
    > div:first-child
    > div
    > svg
    * {
    display: none;
  }

  [data-cy="chessboard"]
    > div:first-child
    > div:first-child
    > div:first-child
    > div:nth-child(2),
  [data-cy="chessboard"]
    > div:first-child
    > div:first-child
    > div:first-child
    > div:nth-child(3) {
    background-color: #ffff0055 !important;
    opacity: 1 !important;
  }
}

@-moz-document domain("chesstempo.com") {
