style.css:
@-moz-document url-prefix("https://flow-control.produs1.ciscoccservice.com/flow/") {
div.diagram-pane__canvas__react-diagrams {
    /* my own custom and preferred grid line spacing */
    /* this fits the new phone contact activity perfectly */
    background-size: 84px 84px, 90px 90px, 14px 14px, 15px 15px;

    /* canvas background color */
    /* this matches the default background color */
    background-color: #ededed;

    /* my own custom and preferred grid line color and thickness */
    background-image: linear-gradient(#d3d3d3 4px, transparent 0),
      linear-gradient(90deg, #d3d3d3 4px, transparent 0),
      linear-gradient(#d3d3d3 1px, transparent 0),
      linear-gradient(90deg, #d3d3d3 1px, transparent 0);
  }
}
