style.css:
@-moz-document domain("kwork.ru") {
h1, h2, h3, h4, span, .send-request__block-title, p {
	color: #ddd;
}

body {
	background: #111;
	color: #fff;
}

.subnav, .header {
	background: #222;
	border-color: #333 !important;
}

.card {
	background: #222;
	color: #ddd;
	border: none;
	border-radius: 10px;
}

.query-card__footer {
	border-color: #333;
}

.wants-card__header-title, .wants-card__header-title a {
	color: #0b0;
}

a, a.color-text:hover {
	color: #3b9cff;
}

div.offer-individual__full-container {
	border: none;
}

.white-bg-border {
	background: #222;
	color: #bbb;
	border: none;
	border-radius: 10px 10px 0 0;
}

div.offer-individual__bottom-block {
	background: #222;
	border-radius: 0 0 10px 10px;
	border: none;
}

.input, .chosen-container-single .chosen-single {
	background: #111;
	color: #eee;
	border-color: transparent;
	border-radius: 5px;
}


.chosen-container-single .chosen-single span {
	color: #fff !important;
}

.chosen-container .chosen-drop, .chosen-container-active.chosen-with-drop .chosen-single {
	background: #222;
	border-color: #333;
}

.chosen-container .chosen-results {
	color: #ccc;
}

.form-item__error, .offer-individual__item-error {
	color: #ff9393;
}

.mw-100 {
	filter: invert(0.6);
}

.bgWhite {
	background: #222 !important;
}

.modal-design .modal-content, .modal-design .modal-header {
	background: #333;
	color: #ddd;
}

.modal-design .modal-header .close {
	filter: invert(1);
}

.modal-design .modal-body, .commission-modal__table, .commission-modal .offer-faq-content p, .commission-modal .offer-faq-list li {
	color: #ddd;
}

.commission-modal__top-title, .modal-design .modal-header h5, .commission-modal .offer-faq-header, .commission-modal__table thead td, .commission-modal__table thead th {
	color: #fff;
}

.commission-modal__top-text {
	color: #aaa;
}

.commission-modal__table td, .commission-modal__table th, .commission-modal__table, .commission-modal .offer-faq-item {
	border-color: #555;
}

.commission-modal__table tr.disabled td {
	background: transparent;
}

.commission-modal .offer-faq-title {
	color: #2477ff;
}

.preloader__bg {
	background: #0004
}

.tooltipster-sidetip.tooltipster-light .tooltipster-content, .block-state-active_tooltip, .settings-finances__purse-inner-tooltip, .vue-tooltip-theme.tooltip .tooltip-inner {
	background: #333;
	color: #ccc;
	border-radius: 10px;
	border: none;
}

.tooltipster-sidetip.tooltipster-light .tooltipster-box {
	border: none;
	background: none;
}

.tooltipster-sidetip.tooltipster-light.tooltipster-bottom .tooltipster-arrow-background, .vue-tooltip-theme.tooltip[x-placement^=bottom] .tooltip-arrow {
	border-bottom-color: #333;
	color: #333;
}

.tooltipster-sidetip.tooltipster-light.tooltipster-right .tooltipster-arrow-background, .block-state-active .block-state-active_tooltip:after, .block-state-active .block-state-active_tooltip:before, .settings-finances__purse-inner-tooltip:before {
	border-right-color: #333;
	color: #333;
}

.tooltipster-sidetip.tooltipster-light.tooltipster-left .tooltipster-arrow-background {
	border-left-color: #333;
	color: #333;
}

.tooltipster-sidetip.tooltipster-light.tooltipster-top .tooltipster-arrow-background, .vue-tooltip-theme.tooltip .tooltip-arrow {
	border-top-color: #333;
	color: #333;
}

.button-go-top {
	background: #fff;
}

#quick-faq {
	display: none;
}

.header .headertop-desktop .dropdownbox--profile {
	background: #222;
	color: #fff;
}

.header .headertop-desktop .user-popup a {
	color: #eee !important;
}

.header .headertop-desktop .user-type, .header .headertop-desktop .user-popup-item-logout {
	border-color: #555;
}

.chosen-style .chosen-container .chosen-single {
	background: #111 !important;
	color: #fff !important;
	border: none !important;
}

.chosen-style .chosen-container .chosen-single span {
	color: #fff !important;
}

.chosen-style .chosen-container .chosen-results li {
	background: #111;
	color: #ccc !important;
}

.settings-schedule__body .chosen-style .chosen-container .chosen-single span, .settings-schedule__body .chosen-style .chosen-container .chosen-results li {
	color: #fff !important;
}

.chosen-style .chosen-container .chosen-results li.highlighted, .chosen-style .chosen-container .chosen-results li.result-selected {
	background: #444;
	color: #fff !important;
}

.k-select.v-select .vs__dropdown-toggle {
	background: #111;
	border: none;
	color: #fff;
}

.text-body, .v-select .vs__selected {
	color: #fff !important;
}
}

@-moz-document domain("kwork.ru") {
/* HEADER */
.sub-menu-parent .menulist {
	background: #333;
	border-color: #111;
	border-radius: 0 0 10px 10px;
	border: none;
}

.header .headertop-desktop .general-search.custom-search input.form-control {
	background: #111;
	border: none;
	color: #fff;
}

#search-keyword .dropdown, .general-search.custom-search .dropdown {
	background: #222;
	border-color: #111 !important;
}

.custom-search .dropdown-suggestions .suggestion span b {
	color: #fff;
}

.custom-search .dropdown-suggestions .suggestion span {
	color: #aaa;
}

.custom-search .dropdown-suggestions .suggestions .suggestion-user, .custom-search .dropdown-suggestions .suggestions-header {
	background: #333;
	color: #aaa;
}
}

@-moz-document domain("kwork.ru") {
.track-page__header {
	border: none;
	border-radius: 10px;
	overflow: hidden;
	background: #222;
}

.track-page > .contentArea {
	background: transparent !important;
}

.track-spacer {
	background: #111;
}

.waveborder.bottom, .waveborder.top {
	display: none
}

.track-page__header-image img {
	border: none;
}

.track-page__header-h1 {
	color: #fff;
}

.pull-left, .order-info__time, .order-info__time--total {
	color: #ddd;
}

.mr10 {
	filter: invert(0.85);
}

.order-info__line div {
	background: #333;
}

.order-info thead {
	background: #333;
	border-radius: 10px;
	color: #ddd;
}
}

@-moz-document domain("kwork.ru") {
.page-projects--new .connects-points__email, .page-projects--new .connects-points__point {
	background: #222;
	color: #fff;
	border: none;
	border-radius: 10px;
}

.page-projects--new .connects-points__item-link {
	color: #eee;
}

.page-projects--new .connects-points__item-text, .page-projects--new .connects-points__item-count {
	color: #ccc;
}

.wants-filter {
	background: #222;
	border-radius: 10px;
	border-color: transparent;
	color: #fff;
}

.projects-filter__radio {
	color: #ccc;
}

.twin-radio-switch {
	background: #111;
	border: none;
}

input:checked + .twin-radio-switch__label {
	background: #222;
}

.checkbox-after-label label:before {
	background: #111;
	border: none;
}

.checkbox-after-label--kw input:checked + label:before {
	background: #177de5;
}

.projects-filter__item .filter-input {
	background: #111;
	border-radius: 5px;
	border: none;
	color: #ccc;
}

.checkbox-after-label--kw label {
	color: #ccc;
}

.exchange-stats {
	background: #222;
	border-radius: 10px;
	border: none;
}

.tooltip_circle--light {
	background: #222;
	border-color: #333;
}

.tooltip_circle--light.tooltip_circle--hover:hover, .tooltip_circle--light.tooltip_circle_hover {
	background: #333 !important;
	border-color: #444 !important;
}

.page-projects--new .wants-filter__links {
	background: #222;
	border-radius: 0 10px 10px 0;
	border: none;
	overflow: hidden;
}

.page-projects--new .wants-filter__link {
	color: #ccc;
}

.paging ul li .active {
	border: none;
	background: #333;
	border-radius: 10px;
	color: #fff;
}

.paging ul li a.next:hover, .paging ul li span.next:hover, .paging ul li a.prev:hover, .paging ul li span.prev:hover {
	border-radius: 10px;
	background-color: #333;
}
}

@-moz-document domain("kwork.ru") {
/* ЗАКАЗЫ */
div.orders * {
	border-color: transparent !important;
}

.order-list-table .table-style thead td, .order-list-table .table-style thead th {
	background: #222;
}

.order-list-table .table-style tbody tr.m-hidden td {
	background: #222;
}

.order-list-table .table-style {
	border-radius: 10px;
	overflow: hidden;
}

.order-list-table .table-style tbody tr.m-hidden td .user-name, .order-list-table .table-style tbody tr.m-hidden td {
	color: #ccc;
}

.order-list-table tbody tr:hover > td {
	background: #333 !important;
}

.kw-note-tooltip.tooltipster-sidetip.tooltipster-light .tooltipster-content {
	color: #ccc;
}

.k-modal-content__header, .k-modal-content__body, .k-modal-content__footer {
	background: #111;
}

.note-modal .trumbowyg-box .trumbowyg-editor {
	background: #222;
	color: #ccc !important;
}

.kw-button--gray-border:not(.kw-button--inactive):not(.kw-button--waiting) {
	background: #333;
	color: #ccc;
}

.kw-button--gray-border:not(.kw-button--inactive):not(.kw-button--waiting):hover {
	background: #444;
	color: #fff;
}

.kw-button--green:not(.kw-button--inactive):not(.kw-button--waiting):hover {
	background-color: #058309;
}

.search-field--orders .search-field__input {
	border-radius: 8px;
	background: #222;
	border: none;
	color: #fff;
}

.order-tabs__item-count {
	background: #333;
	color: #ccc;
}

.order-tabs:before {
	background: #555;
}
}

@-moz-document domain("kwork.ru") {
.file-list__item--not-image {
	background: #111;
	border-radius: 10px;
	border: none;
}

.file-list__item .finfo .fname {
	color: #ccc;
}

.file-list-image__container, .file-list-image__wrapper {
	background: #111;
	border: none;
}

.t-profile-link {
	color: #fff !important;
}

.track-page__date-hr:before, .track-page__date-hr span {
	background: #333;
	border: none;
}

.request-not-correspond {
	background: #111;
	border: none;
	border-radius: 10px;
}

.request-not-correspond__table-cell {
	background: #111;
	border-color: #444;
}

.dot-user-offline {
	background: #000;
}

.track-sidebar__item {
	background: #222;
	border-radius: 10px;
	border: none;
}

.track-sidebar__item.open .track-sidebar__item-title {
	border-radius: 10px 10px 0 0;
}

.track-sidebar__files .order-files-aside, .track--questions {
	background: #222;
	border: none;
	border-radius: 0 0 10px 10px;
}

.order-files .order-files-link:not(:last-child):after, .track--questions__item {
	border-color: #444;
}

.track--questions__item {
	color: #ccc;
}

.track-sidebar__head, .track-sidebar__item-title, .note--order .note__content {
	background: #222;
	border-radius: 10px;
	border: none;
}

.track--info__user-link {
	color: #fff;
}

.badge--green span {
	color: #000;
}

.track--info__user, .track--progress {
	border-color: #111;
}

.trumbowyg-style--textarea .trumbowyg-box .trumbowyg-editor {
	background: #111;
	color: #ccc !important;
	border: none;
}

.track-item .tr-track .text_message .cm-offer-quote {
	background: #111;
	border: none;
	border-radius: 10px;
}

.progress-line--item__icon:before {
	filter: invert(1);
}

.track--item__sidebar-image {
	background-color: #00341d;
}

.track--item__user .track-item:last-of-type:after {
	border-color: #444;
}

.mf-form .mf-comment-box:not(.is-review) {
	border-radius: 0 0 10px 10px;
	background: #222;
	border-color: #444 !important;
}

.sortable-card-list .draggable-block .clickable .image {
	background: #111;
	border: none;
	border-radius: 10px;
}
}

@-moz-document domain("kwork.ru") {
.form-item--bordered {
	background: #222;
	border: none;
	border-radius: 10px;
}

.form-item label.form-item__label--size-16, .form-item__label.form-item__label--size-16 {
	color: #fff;
}

.input-style, .textarea-style {
	background: #111;
	border: none;
	color: #fff;
}

.portfolio-upload-modal__info-panel {
	background: #111;
	color: #cccc;
}

.portfolio-cover .portfolio-upload-modal__upload-image, .sortable-card-list--is-cross .portfolio-upload-modal__upload-image {
	background: #111;
	border: none;
	padding: 0;
}

.portfolio-upload-modal .form-item--portfolio-cover, .portfolio-upload-modal .form-item--portfolio-videos {
	padding-bottom: 12px;
}

.portfolio-upload-modal .portfolio-cover .cover-upload-mini {
	background: #111;
	border: none;
	border-radius: 10px;
}
}

@-moz-document domain("kwork.ru") {
.dashboard__block {
	background: #222;
	border: none;
	border-radius: 10px;
}

.kw-header--h4, .kw-header--h5, .dashboard__user-card-name, .color-text {
	color: #fff;
}

.dashboard__user-card-stats-title, .kw-progress-bar__value, .dashboard__steps-list-stats {
	color: #aaa;
}

.dashboard__user-card-stats, .table-style td, .dashboard__chat-header {
	border-color: #444;
}

.table-style thead {
	background: #333;
	border: none;
	color: #fff;
}

.kw-progress-bar__chart:before {
	background: #111;
	border: none;
}

.chat__list-user {
	color: #ccc;
}

.chat__list-message {
	color: #aaa;
}

.chat__list-item:hover {
	background: #333;
}
}

@-moz-document domain("kwork.ru") {
.user-page-head, .bgLightGray, .bodybg {
	background: #111;
}

.user-col-container {
	background: #333 !important;
	border: none;
	border-radius: 10px;
}

.user-col-container .user-col.user-col-right {
	background: #333;
	border-radius: 0 10px 10px 0;
	border-color: #555;
}

.user-col-container .user-profession, .user-col-container .user-info {
	color: #fff;
}

.user-col-container .user-avatar-container {
	background: #111;
	border: none;
	border-radius: 10px;
}

.user-col-container .user-avatar-image {
	border-radius: 5px;
	border: none;
	overflow: hidden;
}

.portfolio-card-collage.portfolio-card-collage--add, .portfolio-card-collage-wrap .portfolio-card-collage {
	background: #333;
	border-color: #333;
	border-radius: 10px;
	overflow: hidden;
}
}

@-moz-document domain("kwork.ru") {
.chat {
	border-radius: 10px;
	border: none;
	overflow: hidden;
	background: #222;
}

.chat .kwork-conversation__list {
	background: #222;
}

.chat__conversation-empty img {
	filter: invert(1);
}

.chat__search, .chat__conversation-wrapper, .kwork-conversation__header {
	background: #222;
	border-color: #444;
}

.kwork-conversation__header-title-link .bold, .kwork-conversation__header-title-link.bold {
	color: #fff !important;
}

.chat__list-item_active {
	background: #111 !important;
}

.chat__sound-icon svg path[fill="#fff"], .kwork-conversation__header-menu-icon path[fill="#fff"] {
	display: none;
}

.chat__search-inner {
	background: #111;
	border-color: #444;
}

.chat__search_focused .chat__search-input {
	color: #ccc;
}

.mf-form .mf-message-row .mf-message-input {
	background: #333;
	border: none;
}

.chat .mf-form .mf-message-row .mf-message-input .mf-message .trumbowyg-editor {
	color: #fff !important;
}

.chat .messages-list .content-offer-c--accept {
	background: #04b70a22;
	color: #ccc;
}

.kwork-user-status {
	border-color: #222;
}

.kwork-conversation__header-menu-btn:hover {
	background-color: #111;
}

.kwork-conversation__header-menu {
	background: #333;
	border-radius: 10px;
	overflow: hidden;
}

.kwork-conversation__header-menu-item:not(.kwork-conversation__header-menu-item-note):hover {
	background: #444;
}
}

@-moz-document domain("kwork.ru") {
.settings-block {
	background: #222;
	border-radius: 10px;
	border-color: transparent;
}

.form-item label {
	color: #fff;
}

.phone-setting-block__field {
	background: #111;
	border: none;
	color: #555;
}

.trumbowyg-style--input .trumbowyg-box {
	background: #111;
	border: none !important;
	color: #fff;
}

.trumbowyg-style .trumbowyg-box .trumbowyg-editor, .form-item label, .form-item__label {
	color: #fff !important;
}

.settings-tabs__item, .settings-finances__purse-label {
	color: #ccc;
}
}

@-moz-document domain("kwork.ru") {
.footer__menu-title {
	color: #fff;
}

.footer__menu-links > a {
	color: #aaa;
}

.footer__logo:before {
	content: '';
	display: block;
	width: 120px;
	height: 22px;
	background: url(https://cdn-edge.kwork.ru/images/header/logo-dark.svg?ver=2);
	background-origin: contain;
	background-repeat: no-repeat;
}

.footer__logo-image {
	display: none;
}
}
