style.css:
@-moz-document domain("zhihu.com") {
.TopstoryMain,
.Topstory-mainColumn,
.SearchMain,
.Question-main,
.Question-mainColumn,
.SearchTabs-inner
{
    width: 800px; /* 设置容器的宽度 */
    margin: 0 auto; /* 水平居中 */
}

.css-knqde,
.css-1qyytj7{
display: none;
}
}

@-moz-document domain("zhihu.com"), regexp(".*.zhihu.com/question?.*") {
.QuestionHeader-main{
  width: 800px;
  margin: 0px 0px 0px 80px;
}

.QuestionHeader-content{
  flex-direction: column;
  margin: 0 auto;
}
}

@-moz-document domain("zhihu.com"), regexp(".*.zhihu.com/people?.*") {
.Profile-main{
 flex-direction: column;
}

.Profile-mainColumn{
   width: 80%;
   margin: 0 auto;
}
}

@-moz-document domain("zhihu.com"), regexp(".*.zhihu.com/collection?.*") {
.CollectionsDetailPage{
  flex-direction: column;
}

.CollectionsDetailPage-mainColumn{
   width: 80%;
   margin: 0 auto;
}
}
