/* Useless CSS Code */

body {
    transform: rotate(180deg);
    background: repeating-linear-gradient(45deg, #ff0000, #ff0000 10px, #00ff00 10px, #00ff00 20px);
    animation: spin 5s infinite;
}

@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

/* Adds a disco effect to text */
h1 {
    font-family: 'Comic Sans MS', 'Chalkboard SE', 'Comic Neue', cursive;
    animation: colorChange 2s infinite;
}

@keyframes colorChange {
    0% { color: red; }
    25% { color: blue; }
    50% { color: green; }
    75% { color: yellow; }
    100% { color: red; }
}

/* Makes all images upside down */
img {
    transform: scaleY(-1);
}

/* Hides the cursor and adds a trail of hearts */
* {
    cursor: none;
    pointer-events: none;
}

body::before {
    content: 'â™¥';
    position: fixed;
    font-size: 2em;
    color: red;
    animation: hearts 0.5s infinite linear;
}

@keyframes hearts {
    0% { opacity: 0; }
    50% { opacity: 1; }
    100% { opacity: 0; }
}
