style.css:
/* Set a pastel background color */
body {
  background-color: #fce6ea; /* Light pink */
}

/* Change text color to a soft pastel */
body, p, h1, h2, h3, h4, h5, h6 {
  color: #8a6b8a; /* Light purple */
}

/* Add a cute font style */
body {
  font-family: 'Comic Sans MS', cursive, sans-serif;
}

/* Add some magical effects to links */
a {
  color: #ff69b4; /* Hot pink */
  text-decoration: underline;
}

/* Change button styles */
button, input[type="button"], input[type="submit"] {
  background-color: #ffb6c1; /* Light pink */
  color: #fff; /* White */
  border: 2px solid #ff69b4; /* Hot pink */
  border-radius: 5px;
  padding: 8px 12px;
  cursor: pointer;
}

/* Add cute hover effect to buttons */
button:hover, input[type="button"]:hover, input[type="submit"]:hover {
  background-color: #ff69b4; /* Hot pink */
  color: #fff; /* White */
}

/* Add a magical border to images */
img {
  border: 3px solid #ffb6c1; /* Light pink */
  border-radius: 10px;
}

