style.css:
@-moz-document url-prefix("https://classroom.google.com/") {
/* Set background color for specific elements */
    .QRiHXd,
    .XIpEib,
    .WpHeLc {
        background-color: #ffe7f6 !important;
    }

    /* Set text color for specific elements */
    .rpo4wf-J3yWx, 
    .Pce5Kb,
    .YVvGBb, 
    .z3vRcc-J3yWx {
        color: #ffb4da !important; 
    }

    /* Replace specified element with image */
    .UAJaRc::before {
        content: "";
        position: fixed;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background-image: url('https://i.imgur.com/M67Yy15.png') !important;
        background-repeat: no-repeat;
        background-size: cover;
        z-index: 9999 !important; /* Ensure it's above other elements */
    }
}
